/********************************************************************************
* 기      능   : 행 추가
********************************************************************************/
function fn_plus(obj)
{
	switch ( substr(right(obj,2),0,1) )
	{
		//재료비 행추가
		case 1 : 
			var newRow = gfn_addGridRow(grd_list, -1,"FLAG");
			ds_list_1.SetColumn(newRow,"CHECK","1");
			ds_list_1.SetColumn(newRow,"GUBN","1");
			ds_list_1.SetColumn(newRow,"QTNUM",fa_qtnum);
		break;

		//가공비 행 추가
		case 2 :
			var newRow = gfn_addGridRow(grd_list2, -1,"FLAG");
			ds_list_2.SetColumn(newRow,"CHECK","1");
			ds_list_2.SetColumn(newRow,"GUBN","2");
			ds_list_2.SetColumn(newRow,"QTNUM",fa_qtnum);
		break;

		//표면처리비 행 추가
		case 3 :
			var newRow = gfn_addGridRow(grd_list3, -1,"FLAG");
			ds_list_3.SetColumn(newRow,"CHECK","1");
			ds_list_3.SetColumn(newRow,"GUBN","3");
			ds_list_3.SetColumn(newRow,"QTNUM",fa_qtnum);
		break;

		//기타 행 추가
		case 4 :
			var newRow = gfn_addGridRow(grd_list4, -1,"FLAG");
			ds_list_4.SetColumn(newRow,"CHECK","1");	
			ds_list_4.SetColumn(newRow,"GUBN","4");
			ds_list_4.SetColumn(newRow,"QTNUM",fa_qtnum);
		break;

	}
}
/********************************************************************************
* 기      능   :  행삭제 minus
********************************************************************************/
function fn_minus(obj)
{
	var a = substr(right(obj,2),0,1);

	switch ( a )
	{
	   //재료비 행 삭제( Flag 값이 I 일 때는 그리드 행 삭제하고, 빈 값일 때는 Flag D 로 SetColumn)
		case 1 : 
			 ds_list_1.fireevent = false;
			 if ( ds_list_1.rowcount() == 0 ) {
				gfn_showMsg("CW00013");
				return;
			}

			if(ds_list_1.GetColumn(ds_list_1.currow,"FLAG") == "I"){
				ds_list_1.DeleteRow(ds_list_1.currow);

			} else {
				ds_list_1.SetColumn(ds_list_1.currow,"FLAG","D");
			}

			ds_list_1.fireevent = true;
		break;

		//가공비 행 삭제( Flag 값이 I 일 때는 그리드 행 삭제하고, 빈 값일 때는 Flag D 로 SetColumn)
		case 2 :

			ds_list_2.fireevent = false;
			if ( ds_list_2.rowcount() == 0 ) {

				gfn_showMsg("CW00013");
				return;
			}

			if(ds_list_2.GetColumn(ds_list_2.currow,"FLAG") == "I"){
				ds_list_2.DeleteRow(ds_list_2.currow);

			} else {
				ds_list_2.SetColumn(ds_list_2.currow,"FLAG","D");
			}

			ds_list_2.fireevent = true;

		break;

		//표면처리비 행 삭제( Flag 값이 I 일 때는 그리드 행 삭제하고, 빈 값일 때는 Flag D 로 SetColumn)
		case 3 :
			ds_list_3.fireevent = false;
			if ( ds_list_3.rowcount() == 0 ) {

				gfn_showMsg("CW00013");
				return;
			}

			if(ds_list_3.GetColumn(ds_list_3.currow,"FLAG") == "I"){
				ds_list_3.DeleteRow(ds_list_3.currow);

			} else {
				ds_list_3.SetColumn(ds_list_3.currow,"FLAG","D");
			}

			ds_list_3.fireevent = true;
		break;

		//기타 행 삭제( Flag 값이 I 일 때는 그리드 행 삭제하고, 빈 값일 때는 Flag D 로 SetColumn)
		case 4 :
			ds_list_4.fireevent = false;
			if ( ds_list_4.rowcount() == 0 ) {

				gfn_showMsg("CW00013");
				return;
			}

			if(ds_list_4.GetColumn(ds_list_4.currow,"FLAG") == "I"){
				ds_list_4.DeleteRow(ds_list_4.currow);

			} else {
				ds_list_4.SetColumn(ds_list_4.currow,"FLAG","D");
			}

			ds_list_4.fireevent = true;

		break;
	}
}

/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - 재료비
********************************************************************************/
function ds_list_1_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	//Grid 에서 컬럼 값 변경 시 Flag 처리
	gfn_changeFLAG( obj, strColumnID, nRow, "FLAG");

	//재료비 행에서 실소요량, Loss 수량 변경할 때마다 합계수량 계산
	if ( strColumnID == "BDMNG" || strColumnID == "LOSS"){

		var a = ToFloat(ds_list_1.GetColumn(nRow,"BDMNG"));
		var b = ToFloat(ds_list_1.GetColumn(nRow,"LOSS"));

		ds_list_1.SetColumn(nRow,"TOT_MENGE",a+b);

	}

	// 합계수량 및 단가 변경될 때마다 합계 금액 계산
	if ( strColumnID == "TOT_MENGE" || strColumnID == "NETPR" || strColumnID == "MENGE" ) 
	{

		var c = ToFloat(ds_list_1.GetColumn(nRow,"NETPR"));
		var d = ToFloat(ds_list_1.GetColumn(nRow,"TOT_MENGE"));
		var e = ToFloat(ds_list_1.GetColumn(nRow,"MENGE"));

		ds_list_1.SetColumn(nRow,"TOT_NETPR",c*d*e);
		fn_cost();
	}


}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - 가공비
********************************************************************************/
function ds_list_2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	//Grid 에서 컬럼 값 변경 시 Flag 처리
	gfn_changeFLAG( obj, strColumnID, nRow, "FLAG");

	//수량, 단가 변경될 때마다 총 금액 계산
	if ( strColumnID == "MENGE" || strColumnID == "NETPR"){

		var a = ToFloat(ds_list_2.GetColumn(nRow,"MENGE"));
		var b = ToFloat(ds_list_2.GetColumn(nRow,"NETPR"));

		ds_list_2.SetColumn(nRow,"TOT_NETPR",a*b);

		fn_cost();
	}
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - 표면처리비
********************************************************************************/
function ds_list_3_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	//Grid 에서 컬럼 값 변경 시 Flag 처리
	gfn_changeFLAG( obj, strColumnID, nRow, "FLAG");

	//수량, 단가 변경될 때마다 총 금액 계산
	if ( strColumnID == "MENGE" || strColumnID == "NETPR"){

		var a = ToFloat(ds_list_3.GetColumn(nRow,"MENGE"));
		var b = ToFloat(ds_list_3.GetColumn(nRow,"NETPR"));

		ds_list_3.SetColumn(nRow,"TOT_NETPR",a*b);
		fn_cost();

	}
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - 기타
********************************************************************************/
function ds_list_4_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	gfn_changeFLAG( obj, strColumnID, nRow, "FLAG");
	fn_cost();
}

/********************************************************************************
* 기      능   : 기타 비용 계산(총 금액, 기업이윤, 일반관리비 등등)
********************************************************************************/
function fn_cost() 
{
	var a = ToFloat(ds_list_1.Sum("TOT_NETPR")) + ToFloat(ds_list_2.Sum("TOT_NETPR")) +
	        ToFloat(ds_list_3.Sum("TOT_NETPR")) + ToFloat(ds_list_4.Sum("NETPR"));

	ed_cost1.Value = a * 0.05;
	ed_cost2.Value = (a + (a*0.05)) * 0.05;
	ed_total.Value = a + (a * 0.05) + ((a + (a*0.05)) * 0.05);

}

/********************************************************************************
* 기      능   : 견적서 전송
********************************************************************************/
function fn_send(obj)
{
	if (!gfn_showConfirm("CI00010"))
	{ 
		return;
	}

	// 견적서 상태값 Check
	if(ds_cnt.GetColumn(0,"CNT") <> "1")
	{
		gfn_showAlert("CW00028","견적서 저장 후 전송해주세요.");
		return;

	} 

	tit_clearActionInfo();	
	tit_addSingleActionInfo("mm08:MM0801001B_U6");
	tit_addSingleActionInfo("mm08:MM0801001B_U8");

	tit_callService(
		""
		, ""
		, "ds_exCond=ds_exCond"
		, ""
		, ""
		, "fn_afterSave"        
		); 
}
/********************************************************************************
* 기      능   : 견적서 전송취소
********************************************************************************/
function fn_cancel(obj)
{
	if (!gfn_showConfirm("CI00011"))
	{ 
		return;
	}
	if(ds_cnt.GetColumn(0,"PRO_F") <> "2")
	{
		gfn_showAlert("CW00022","견적서 상태값");
		return;
	}

	tit_clearActionInfo();	
	tit_addSingleActionInfo("mm08:MM0801001B_U7");
	tit_addSingleActionInfo("mm08:MM0801001B_U9");

	tit_callService(
		""
		, ""
		, "ds_exCond=ds_exCond"
		, ""
		, ""
		, "fn_afterSave"        
		); 

}
/********************************************************************************
* 기      능   : 견적서 출력
********************************************************************************/
function btn_print_OnClick(obj)
{
	if(ds_cnt.GetColumn(0,"PRO_F") <> "2")
	{
		gfn_showAlert("CW00022","견적서 전송상태에서만 출력이 가능합니다. 견적서 상태");
		return;
	}

	ds_print.AppendDataset("ds_list_1");
	ds_print.AppendDataset("ds_list_2");
	ds_print.AppendDataset("ds_list_3");
	ds_print.AppendDataset("ds_list_4");

	Dialog("MM08::MM0801001C.xml","", -1, -1, "Resize=true");
}
