/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	//필수 입력 항목 체크 
	if ( gfn_isNull(cal_date_fm, true) ) {
		gfn_showMsg("CW00001","발주일");
		return;
	}
	if ( gfn_isNull(cal_date_to, true) ) {
		gfn_showMsg("CW00001","발주일");
		return;
	}
	if ( !gfn_checkDate( cal_date_fm.value, cal_date_to.value, true, cal_date_to) ) {
		return;
	}

	// 특정edit에 숫자나 영문이 들어가지 않으면 조회 불가 200918 yj
	if (chk_edcode(trim(ed_posid.Text)) == false) {
		alert("보수WBS에는 영문과 숫자만 입력 가능합니다.");
		return false;
	}
	else if (chk_edcode(trim(ed_matnr.Text)) == false) {
		alert("자재번호에는 영문과 숫자만 입력 가능합니다.");
		return false;
	}

	var maxdate = gfn_getAfterDate(cal_date_fm.value, 180);
	var message_arg = ["조회기간","180일"];
	if (cal_date_to > maxdate)
	{
		gfn_showAlert("CW00133",message_arg);
		return;
	}

//	trace(ds_cond.saveXML());
	//조회
	tit_clearActionInfo();
	tit_addSearchActionInfo("mm11:MM1101001A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);


}

