/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {

   	if ( ds_list.rowCount() > 0 ) {
		gfn_showMsg("CI00002", ds_list.rowCount() + "");

	} else {
		gfn_showMsg("CW00010");
	}

	if(ck_notpo.Value == "1") {
		btn_createpo.Enable = true;
		btn_cancelpo.Enable = false;
	} else {
		btn_createpo.Enable = false;
		btn_cancelpo.Enable = true;
	}

}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {

	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		ds_list.fireevent = true;
	}
	//trace(ds_list2.saveXML());
	fn_query();

}

