function btn_createpo_OnClick(obj)
{
	//  자료가 없으면 리턴
	if (ds_list.rowcount < 1) {
		gfn_showAlert("CW00010");
		return;
	}

	//저장여부 확인
	// if (gfn_showConfirm("CI30001", "발주 생성 하시겠습니까??")) {
		// return;
	// }	

	if (!gfn_showConfirm("CI00008")) {
		return;
	}


	ds_list.fireevent = false;
	gfn_copyDs(ds_list, ds_list2, "CHECK=1");	

	var cnt = ds_list2.GetRowCount();
alert(ds_list2.GetColumn(i,"LNAME"));
	if (cnt > 0)
	{
		for(var i=0; i < ds_list2.rowcount; i++)
		{
			if ( ds_list2.GetColumn(i,"LNAME") = null)
			{
				alert("발주 할 업체를 선택하세요.");
				return;
			}

			//trace(ds_list2.saveXML());
			if ( ds_list2.GetColumn(i,"LIFNR") == "")
			{
				alert("발주 할 업체를 선택하세요.");
				return;

				ds_list2.SetColumn(i,"LIFNR",ds_list2.GetColumn(i,"LIFNR_A"));
				ds_list2.SetColumn(i,"LNAME",ds_list2.GetColumn(i,"LNAME"));
			}

			if( ds_list2.GetColumn(i,"DFLAG") == "X")
			{
				gfn_showAlert("CW00017");
				ds_list.fireevent = true;
				return;	
			}

			// if ( ds_list2.GetColumn(i,"LIFNR") == "" || ds_list2.GetColumn(i,"LNAME") == "")
			// {
				// gfn_showMsg("CW00017");
				// ds_list.fireevent = true;
				// return;	
			// }
		}
	}

	ds_list.fireevent = true;

	// 저장 처리 
	tit_clearActionInfo();
	ds_key.ClearData();

	tit_addSearchActionInfo("mm11:MM1101001A_S2",true);

	tit_addSelectExActionInfo(
		"mm11:MM1101001A_S3"
	   ,"mm11:MM1101001A_I1"
	   ,"");	

	//   call 서비스에서 commit실행하여 commit 필요없음.
	tit_callService(
        ""
        , ""
        , "ds_list2=ds_list2 ds_cond=ds_cond ds_key=ds_key"
        , "ds_key=ds_key"
        , ""
        , "fn_afterSave");


	// tit_addSaveActionInfo(
        // "mm03:MM0301002A_I1"
        // , ""
        // , ""
        // , "FLAG"
		// ,""
		// , 0
		// , "");

//   call 서비스에서 commit실행하여 commit 필요없음.

	// tit_callService(
        // ""
        // , ""
        // , "ds_list=ds_list_0:A ds_cond=ds_cond ds_key=ds_key"
        // , "ds_key=ds_key"
        // , ""
        // , "fn_afterSave");

	// tit_addSelectExActionInfo(
		// "mm11:MM1101001A_S2"
	   // ,"mm11:MM1101001A_I1"
	   // ,"mm11:MM1101001A_U1");

	// tit_callService(
        // ""
        // , ""
        // , "ds_list2=ds_list2:A"
        // , ""
        // , ""
        // , "fn_afterSave");	
}
