function btn_cancelpo_OnClick(obj)
{
//  자료가 없으면 리턴
	if (ds_list.rowcount < 1) {
		gfn_showAlert("CW00010");
		return;
	}

	//저장여부 확인
	// if (gfn_showConfirm("CI30001", "발주 생성 하시겠습니까??")) {
		// return;
	// }	

	if (!gfn_showConfirm("CI00008")) {
		return;
	}

	ds_list3.ClearData();
	ds_list.fireevent = false;
	gfn_copyDs(ds_list, ds_list3, "CHECK=1");	

	var cnt3 = ds_list3.GetRowCount();

	if (cnt3 > 0)
	{
		for(var i=0; i < ds_list3.rowcount; i++)
		{
			if ( ds_list3.GetColumn(i,"STATUS2") > "2")
			{
				gfn_showAlert("CW00022","거래명세서 생성 됐을  경우 발주취소 할 수 없습니다. 상태값");
				ds_list.fireevent = true;
				return;	
			}
		}
	}

	ds_list.fireevent = true;

	// 저장 처리 
	tit_clearActionInfo();

    //tit_addSearchActionInfo("mm11:MM1101001A_S2",true);
	tit_addMultiActionInfo("mm11:MM1101001A_U1");
	// tit_addSelectExActionInfo(
		 // "mm11:MM1101001A_S3"
	    // ,""
	    // ,"mm11:MM1101001A_U1");	

	  //call 서비스에서 commit실행하여 commit 필요없음.
	tit_callService(
        ""
        , ""
        , "ds_list3=ds_list3"
        , ""
        , ""
        , "fn_afterSave");

}

/********************************************************************************
* 기      능   : 특정edit 입력 문자 검사
********************************************************************************/
function chk_edcode(str) {

	var pattern = "[^A-z0-9 ]+"; // 영문,숫자가 아닌 문자 filtering하는 정규식
	var reg = CreateRegExp(pattern, "g");
	var str_notnum = reg.Exec(str);

	if ( str_notnum.length >0) {
		// 영문,숫자가 아닌 문자 입력 불가
		return false;
	}
	return true;
}
