/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 이중 실행 방지(실행 후 0으로)
    CHK = "0";

    // 조회 결과가 있으면 엑셀 버튼 활성화
	if ( ds_list.rowCount() > 0 ) {
		ufn_enableBtn("excel", true);
		gfn_showMsg("CI00002", ds_sum.rowCount() + "");

		ds_sum.rowposition = 0;
		// ds_list.filter("PROJ == " + quote( ds_sum.GetColumn(0,"POSID")) 
					// +" UDATE =="+ quote( ds_sum.GetColumn(nRow,"UDATE"))
					// +" MATNR =="+ quote( ds_sum.GetColumn(nRow,"MATNR")));
	} else {
		ufn_enableBtn("excel", false);
//		cal_rdate_p.Enable = false;
		gfn_showMsg("CW00010");
	}
	chk = "0";

	if (G_VEND_CODE = "0000009936" || G_USER_ID = "2014408" || G_USER_ID = "2021684" || G_USER_ID = "2025540" || G_USER_ID = "2026562" || 
		G_USER_ID = "3008410" || G_USER_ID = "3007014" || G_USER_ID = "3011402" || G_USER_ID = "3012778" || G_USER_ID = "3008819" || 
		G_USER_ID = "3017354" || G_USER_ID = "3014436" || G_USER_ID = "3015815" || G_USER_ID = "3019233" || G_USER_ID = "3019250" || 
		G_USER_ID = "3020894" || G_USER_ID = "3020908" || G_USER_ID = "3021173" || G_USER_ID = "5018595" || G_USER_ID = "5018714") {
		fn_hide_grid_column("NETPR");
		Button3.Enable = false;
		bt_print.Enable = false;
	}
}

