/********************************************************************************
* 기      능   :  거래명세서 생성
********************************************************************************/
function ufn_makeGeoRae() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	// 필수 입력 항목 체크 
	if ( gfn_isNull(cal_udate_fm, true) ) {
		gfn_showMsg("CW00001",cb_gubn.Text+"(시작)");
		return;
	}
	if ( gfn_isNull(cal_udate_to, true) ) {
		gfn_showMsg("CW00001",cb_gubn.Text+"(종료)");
		return;
	}

	if ( !gfn_checkDate( cal_udate_fm.value, cal_udate_to.value, true, cal_udate_to) ) {
		return;
	}

	if ( gfn_isNull(cb_lgort, true) ) {
		gfn_showMsg("CW00001","저장위치");
		return;
	}

	var arg = "fa_udate_fm=" + quote(cal_udate_fm.Value);
	arg += " fa_udate_to=" + quote( cal_udate_to.Value) ;
	arg += " fa_werks=" + quote( cb_werks.Value );
	arg += " fa_lgort=" + quote( cb_lgort.Value );
	arg += " fa_ekgrp=" + quote( cb_ekgrp.Value );
	arg += " fa_posid1=" + quote( ed_posid1.Value );
	arg += " fa_posid2=" + quote( ed_posid2.Value );
	arg += " fa_gubn=" + quote( cb_gubn.Value );
	//arg += " fa_knttp=" + quote( cb_knttp.Value );

	gfn_copyDs(ds_sum, ds_sum_0, "CHECK=1");

	var fa_posid = "";
	var fa_udate = "";
	var fa_matnr = "";
	var err[2];
//	var knttp_check = "";
	var asset = "";
	if (ds_sum_0.rowcount > 0) {
		fa_posid += "AND MM013C.POSID1 IN (";
		fa_udate += "AND MM013B.UDATE IN (";
		fa_matnr += "AND MM013B.MATNR IN (";
		for (var i =0; i < ds_sum_0.rowcount ; i++){
			if (ds_sum_0.GetColumn(i,"BIGO") == "1") {
			    err[0] = grd_sum.GetCellProp("head",1,"text");
			    err[1] = ds_sum_0.GetColumn(i,"EBELN");
				gfn_showAlert("CW00184",err);
				return;
			}

			if (i == 0){
				fa_posid +=  "'"+ds_sum_0.GetColumn(i,"POSID")+"'";
				fa_udate +=  "'"+ds_sum_0.GetColumn(i,"UDATE")+"'";
				fa_matnr +=  "'"+ds_sum_0.GetColumn(i,"MATNR")+"'";

			// knttp_check = ds_sum_0.GetColumn(i,"KNTTP");
			 } else if (i == ds_sum_0.rowcount-1) {
				 fa_posid +=  ",'"+ds_sum_0.GetColumn(i,"POSID")+"'";
				 fa_udate +=  ",'"+ds_sum_0.GetColumn(i,"UDATE")+"'";
				 fa_matnr +=  ",'"+ds_sum_0.GetColumn(i,"MATNR")+"'";
			}
			else {
				fa_posid +=  ",'"+ds_sum_0.GetColumn(i,"POSID")+"'";
				fa_udate +=  ",'"+ds_sum_0.GetColumn(i,"UDATE")+"'";
				fa_matnr +=  ",'"+ds_sum_0.GetColumn(i,"MATNR")+"'";
			}
			 // if( i > 0 && knttp_check != ds_sum_0.GetColumn(i,"KNTTP")) 
			 // {
				 // asset = [ds_sum_0.GetColumn(i,"POSID"), "계정범주 불일치 (일반/투자자산/외주임가공 분리 작성 필수)"];
			     // gfn_showAlert("CW00193", asset);
				 // return;
			 // }
		}
		fa_posid += ")";
		fa_udate += ")";
		fa_matnr += ")";
	}
	arg += " fa_posid=" + quote(fa_posid);
	arg += " fa_udate=" + quote(fa_udate);
	arg += " fa_matnr=" + quote(fa_matnr);

	// if(ds_sum_0.GetColumn(ds_sum_0.rowcount-1,"KNTTP") == "F")
	// {
		// arg += " fa_knttp=" + quote(1);
	// }
	// else
	// {
		// arg += " fa_knttp=" + quote( ch_knttp.Value );
	// }

   gfn_openWin("MM1102001A", arg);

}

