// [ PART 6 ]
// component Event 정의 : 각 Component 별 이벤트 
//=========================================================================================
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}

function grd_sum_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		//  전체 선택 해제
		gfn_selectAllGrid(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}


function grd_list_OnCellDBClik(obj,nRow,nCell){
	if (nCell == 3 || nCell==4){
		// 품목표 조회 팝업
		var arg = "HOGIN=" + quote(SubStr(obj.GetCellValue(nRow,3),0,9));
		arg += " MATNR=" + quote(obj.GetCellValue(nRow,5));
		arg += " MATKL=" + quote();

		gfn_openPopup("MM0102002B", true, arg, 420, 529);
	} else if (nCell == 5){
		//자재상세 조회 팝업
		gfn_openPopup("MM0901001A", true, "fa_matnr=" + quote(obj.GetCellValue(nRow,5)), 420, 529);
	}
}


// PO출력
function report_OnClick(obj)
{
	ds_sum.fireevent = false;
	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_sum, ds_sum_0, "CHECK=1");

	if (ds_sum_0.rowcount < 1)
	{
		gfn_showAlert("CW00013");
		ds_sum.fireevent = true;
		return;
	}
	gfn_copyDs(ds_list, ds_list1);

	//trace(ds_list1.saveXML());
	//trace(ds_sum_0.saveXML());
	for (var i =0; i < ds_sum_0.rowcount ; i++){

		for (var j=0; j< ds_list1.rowcount; j++){

			if (ds_list1.GetColumn(j,"EBELN") == ds_sum_0.GetColumn(i,"EBELN")) {
				ds_list1.SetColumn(j,"CHECK","1");
			}

			if (ds_list1.GetColumn(j,"EKGRP") == '106' OR  ds_list1.GetColumn(j,"EKGRP") == '108' OR
	            ds_list1.GetColumn(j,"EKGRP") == '109' OR  ds_list1.GetColumn(j,"EKGRP") == '117')
	        {    
	            ds_list1.SetColumn(j,"R_EKGRP","신동연");
	        } else if (ds_list1.GetColumn(j,"EKGRP") == '110' OR  ds_list1.GetColumn(j,"EKGRP") == '111' OR
	         ds_list1.GetColumn(j,"EKGRP") == '113' OR  ds_list1.GetColumn(j,"EKGRP") == '115') 
	        {
				ds_list1.SetColumn(j,"R_EKGRP","권선미");
	        } else {
				ds_list1.SetColumn(j,"R_EKGRP","김점곤");
	        }

		}
	}

	gfn_copyDs(ds_list1, ds_list2, "CHECK=1");

	ds_sum.fireevent = true;

	// UbiReport Call
	Dialog("MM11::MM1101002B.xml", "", -1, -1, "Resize=true");
}

function grd_sum_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{ 
	ds_list.filter("PROJ == " + quote( ds_sum.GetColumn(nRow,"POSID"))+"&&"+ 
				   "UDATE =="+ quote( ds_sum.GetColumn(nRow,"UDATE"))+"&&"+
				   "MATNR =="+ quote( ds_sum.GetColumn(nRow,"MATNR")));
}


