function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	// 공급 업체명 설정 
	ed_lifnrName.value = G_VEND_NAME;
	ds_cond.setColumn(0, "LIFNR", G_VEND_CODE_M);

//    ds_cond.setColumn(0, "MANDT", G_MANDT);

    // 엑셀 버튼 비활성화
    ufn_enableBtn("excel", false);

    // 코드 DataSet 가져오기
    var codeArr = ["6"];
    var dsObjArr = [ds_pro_f_cd];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);


    // 코드 DataSet 가져오기
    var codeArr = ["10"];
    var dsObjArr = [ds_ekgrp_cd];
    var addTypeArr = [G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

    // 플랜트코드 DataSet 가져오기
    var codeArr = ["16"];
    var dsObjArr = [ds_werks_cd];
    var addTypeArr = [G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

    // 입고통제 자재 DataSet 가져오기
    var codeArr = ["TM"];
    var dsObjArr = [ds_tm];
    var addTypeArr = [G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

    //바코드 옵션 설정
	cb_tagprint.Index = 0;
	cb_tagtype.Index = 0;

	    // 입고통제 예외 관리자 DataSet 가져오기
    var codeArr = ["EPM"];
    var dsObjArr = [ds_epm];
    var addTypeArr = [G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

	//  구매그룹  설정 
	gfn_insertEmptyRow( ds_ekgrp_cd, G_CODE_ALL);
	cb_ekgrp.Index = 0;

	//  Plant  설정 
	cb_werks.Index = 0;

	//  납품예정일 설정 
	cal_rdate_p_fm.value = gfn_getCurrDate();
	cal_rdate_p_to.value = gfn_getAfterDate(cal_rdate_p_fm.value, 15);

}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_pro_f.index = 0;

    //  거래명세서 상세 에서 호출된 경우 조건값 입력
	if ( isExistVar("fa_rdate_p_fm", true) ) {
	   cal_rdate_p_to.value = fa_rdate_p_to;
	   cal_rdate_p_fm.value = fa_rdate_p_fm;

       cb_werks.Value = fa_werks;
	   cb_ekgrp.Value = fa_ekgrp;
	   ed_posid1.Value = fa_posid1;
	   ed_posid2.Value = fa_posid2;	   
	   fn_query();
	}
	// 완료메세지
	gfn_showMsg("CI00007");
}
