/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	var posgbn1 = "";
	var posgbn2 = "";

	var maxdate = gfn_getAfterDate(cal_rdate_p_fm.value, 180);
	var message_arg = ["조회기간","180일"];
	if (cal_rdate_p_to > maxdate)
	{
		gfn_showAlert("CW00133",message_arg);
		return;
	}


	if (length(ed_ebeln.value) == 0 )
	{
		// 필수 입력 항목 체크 
		if ( gfn_isNull(cal_rdate_p_fm, false) ) {
			gfn_showMsg("CW00001","납품예정일(시작)");
			return;
		}
		if ( gfn_isNull(cal_rdate_p_to, false) ) {
			gfn_showMsg("CW00001","납품예정일(종료)");
			return;
		}

		// 전송일자 입력시 체크
		if ( !gfn_isNull(cal_rece_date_fm, false) || !gfn_isNull(cal_rece_date_to, false) ) {
			if ( gfn_isNull(cal_rece_date_fm, false) || gfn_isNull(cal_rece_date_to, false) ) {
				gfn_showMsg("CW00001","전송일자");
				return;
			}
		}

		if ( !gfn_checkDate( cal_rdate_p_fm.value, cal_rdate_p_to.value, true, cal_rdate_p_to) ) {
			return;
		}

		if (length(ed_posid1.value) > 0 && length(ed_posid1.value) < 6){
			gfn_showAlert("CW00092");
			ed_posid1.SetFocus();
			return;
		}

		if (length(ed_posid1.value) >= 6){

			posgbn1 = ed_posid1.value;
			if (length(ed_posid2.value) > 0)
			{
				posgbn2 = gfn_fillChar(ed_posid2.value,11,"Z");
			}
			else
			{
				posgbn2 = gfn_fillChar(ed_posid1.value,11,"Z");
			}
		}
	}

	// 특정edit에 숫자나 영문이 들어가지 않으면 조회 불가 200918 yj
	if (chk_edcode(trim(ed_posid1.Text)) == false) {
		alert("호기번호에는 영문과 숫자만 입력 가능합니다.");
		return false;
	}
	else if (chk_edcode(trim(ed_matnr.Text)) == false) {
		alert("자재번호에는 영문과 숫자만 입력 가능합니다.");
		return false;
	}

	ds_cond.SetColumn(0,"POSID1",posgbn1);
	ds_cond.SetColumn(0,"POSID2",posgbn2);
//	trace(ds_cond.saveXML());
	tit_clearActionInfo();
	if (length(ed_ebeln.value) > 0)	{
		tit_addSearchActionInfo("mm11:MM1102002A_S3");
		tit_addSearchActionInfo("mm11:MM1102002A_S4");
	} else {
		tit_addSearchActionInfo("mm11:MM1102002A_S1");
		tit_addSearchActionInfo("mm11:MM1102002A_S2");
	}

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list ds_sum=ds_sum"
        , ""
        , "fn_afterQuery"
        , true);
}
