/********************************************************************************
* 기      능   : 거래명세서 전송취소 
********************************************************************************/
function fn_cancel() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

    //변경값 확인
	if ( !gfn_isChangeDs(ds_sum, true) ) {
		return;
	}

	ds_list.fireevent= false;

	//  저장에 대한 조건값 처리-PRO_F=2, DDATE=''인것만.
	for ( var i = 0; i < ds_sum.rowcount ; i++ ) {

		if (ds_sum.GetColumn(i,"CHECK") == "1") {
			newNo = ds_sum.getColumn(i, "INVNR");
			gfn_copyDs(ds_list, ds_list3, "INVNR="+quote(newNo));

			for(var j =0; j < ds_list3.rowcount; j++){
				 if ((ds_list3.GetColumn(j, "PRO_F") != "2" && ds_list3.GetColumn(j, "PRO_F") != "5") || length(ds_list3.GetColumn(j, "DDATE")) > 0 ) {
					gfn_showMsg("CW00022",ds_list3.getColumn(j, "INVNR")+"의 상태 또는 삭제일");
					ds_sum.SetColumn(i,"CHECK", "0");
					ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(i,"INVNR")));
					ds_list.fireevent= true;
					return;
				 }
			}
		}
	}
	ds_list.fireevent= true;

	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_sum, ds_sum2, "CHECK=1");

	if (!gfn_showConfirm("CI00011")) {
		return;
	}

	tit_clearActionInfo();

	// zmmt017_M 저장 처리 
	tit_addMultiActionInfo("mm11:MM1102002A_U2");

	// zmmt017 저장 처리 
	tit_addMultiActionInfo("mm11:MM1102002A_U4");

	// zmmt017 _Z 저장 처리 
	tit_addMultiActionInfo("mm11:MM1102002A_U7");

	tit_callService(
        ""
        , ""
        , "ds_sum=ds_sum2:A"
        , ""
        , ""
        , "fn_afterCancel");
}

