/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showSearchMsg(grd_sum);

    // 조회 결과가 있으면 엑셀 버튼 활성화
	if ( ds_sum.rowCount() > 0 ) {
		ufn_enableBtn("excel", true);
		wdate = gfn_getWorkingDate(120, ds_list.GetColumn("0","WERKS"));
		ds_sum.rowposition = 0;

		//ds_tag_detail = "B";

	} else {
		ufn_enableBtn("excel", false);
		gfn_showMsg("CW00010");
	}
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {

	trace(ds_sum2.SaveXML());
			ds_condmts.ClearData();
			for (var i = 0; i <= ds_sum2.GetRowCount()-1; i++) {	
				ds_condmts.AddRow();
				ds_condmts.SetColumn(i, "INVNR", ds_sum2.GetColumn(i,"INVNR"));				
				ds_condmts.SetColumn(i, "G_MANDT", G_MANDT);
			}

			trace(ds_condmts.SaveXML());



			tit_callService(
			  "MM0301002B_ACT"
			, "query3"
			, "ds_condmts=ds_condmts"
			, ""
			, ""
			, "fn_afterSave2"
			, true);




	}
}


function fn_afterSave2() {
		gfn_showMsg("CI00003");
		fn_query();

}



