	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {

	trace(ds_sum2.SaveXML());
			ds_condmts.ClearData();
			for (var i = 0; i <= ds_sum2.GetRowCount()-1; i++) {	
				ds_condmts.AddRow();
				ds_condmts.SetColumn(i, "INVNR", ds_sum2.GetColumn(i,"INVNR"));				
				ds_condmts.SetColumn(i, "G_MANDT", G_MANDT);
			}

			trace(ds_condmts.SaveXML());



			tit_callService(
			  "MM0301002B_ACT"
			, "query4"
			, "ds_condmts=ds_condmts"
			, ""
			, ""
			, "fn_afterCancel2"
			, true);




	}
}

function fn_afterCancel2() {
		gfn_showMsg("CI00003");
		fn_query();

}

