/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_sum_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{ 
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	if ( strColumnID == "CHECK") {
		ds_list.fireevent = false;
		if (obj.GetColumn(nRow, "PRO_F") != "1" ||
		           length(obj.GetColumn(nRow, "DDATE")) > 0) {
			if (obj.GetColumn(nRow, "PRO_F") == "2"){
				gfn_showMsg("CW00027");
				ds_list.fireevent = true;
				return;   
			}
			gfn_showMsg("CW00028","생성 상태가 아닙니다.");
			obj.SetColumn(nRow,"CHECK", "0");
			ds_list.fireevent = true;
			return;
		}
		ds_list.fireevent = true;
	}
}

function grd_sum_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(nRow,"INVNR")));

	// 미수현장 조회 2010.07.08
	var misu = "";
	var mRow = 0;

	if (ds_sum.GetColumn(nRow,"WERKS") = "1000")
	{
		for (var i = 0; i < ds_list.RowCount(); i++) {
			if (ds_list.GetColumn(i,"MISU_GUBN") == "Y")
			{
				mRow = gds_misu.searchRow("I_PSPID== " + quote(substr(ds_list.GetColumn(i,"POSID"),0,6)));

				if ( mRow >= 0) {
					if(gds_misu.getColumn(mRow, "E_MISU") == "1") {
						ds_sum.SetColumn(nRow,"BIGO","2");
						ds_list.SetColumn(i,"BIGO1","2");
					}
				} 
				else{
					misu = gfn_getMisu(substr(ds_list.GetColumn(i,"POSID"),0,6));

					if (misu == "1"){
						ds_sum.SetColumn(nRow,"BIGO","2");
						ds_list.SetColumn(i,"BIGO1","2");
					}
				}
			} 
		}
	}
}

