//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {

	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);	
	fn_init_form();	// 폼 초기화	

    fn_resize();   
	this.onsize = "fn_resize";

}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {	

	gfn_BindColumn(div_detail.div_gubn.ed_sman_cd, "ds_list", "ZCODE"); 	// 구분코드 데이타셋 바인드
	gfn_BindColumn(div_detail.div_gubn.ed_sman_nm, "ds_list", "ZNAME"); 	// 구분코드명 데이타셋 바인드
	gfn_BindColumn(div_detail.div_item.ed_sman_cd, "ds_list", "ITEM_CODE"); 	// 품목코드 데이타셋 바인드
	gfn_BindColumn(div_detail.div_item.ed_sman_nm, "ds_list", "ITEM_NAME"); 	// 품목코드명 데이타셋 바인드
   	gfn_BindColumn(div_detail.div_sman.ed_lifnr, "ds_list", "LIFNR"); 	// 업체코드 데이타셋 바인드
	gfn_BindColumn(div_detail.div_sman.ed_name1, "ds_list", "LIFNR_NAME"); 	// 업체명 데이타셋 바인드

}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {

	ds_cond.ClearData();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "ZCODE", div_gubn.ed_sman_cd.Text);
	ds_cond.SetColumn(0, "ITEM_CODE", div_item.ed_sman_cd.Text);
	ds_cond.setColumn(0, "LIFNR", div_sman.ed_lifnr.Text);

	tit_clearActionInfo();
	tit_addSearchActionInfo("mm12:MM1201001A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능  : 입력 : 맨뒤에 행추가하고 상세정보 항목 초기화
********************************************************************************/
function fn_input() {

	var newRow = gfn_addGridRow(grd_list, "FLAG"); // 행추가 (맨뒤에 추가됨:FLAG(I)) // 추가된 행번호를 받아옴

	div_detail.div_gubn.btn_popSman.enable = true;
	div_detail.div_gubn.ed_sman_cd.enable = true;
	div_detail.div_gubn.ed_sman_nm.enable = true;

	div_detail.div_item.btn_popSman.enable = true;
	div_detail.div_item.ed_sman_cd.enable = true;
	div_detail.div_item.ed_sman_nm.enable = true;

	div_detail.div_sman.btn_popLifnr.enable = true;
	div_detail.div_sman.ed_lifnr.enable = true;
	div_detail.div_sman.ed_name1.enable = true;

}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
