		return false; 
	} // [${}]은/는 필수입력 입니다. 확인바랍니다.

	if ( LIFNR   == null || LIFNR   == "" )
	{ 
		gfn_showAlert("CW00208", "업체" );
		div_detail.div_sman.ed_lifnr.SetFocus(); 	// 업체에 강제 focus처리
