	if ( SPEC    == null || SPEC    == "" ) 
	{
		gfn_showAlert("CW00208", "공급사양 및 규격"); 
		div_detail.ed_spec.SetFocus(); 	// 공급사양 및 규격에 강제 focus처리
		return false; 
	} // [${}]은/는 필수입력 입니다. 확인바랍니다.

	if ( EMPLOYEE    == null || EMPLOYEE    == "" ) 
	{
		gfn_showAlert("CW00208", "총 직원 수"); 
		div_detail.ed_employee.SetFocus(); 	// 총 직원 수에 강제 focus처리
		return false; 
	} // [${}]은/는 필수입력 입니다. 확인바랍니다.

	if ( WORKER    == null || WORKER    == "" ) 
	{
		gfn_showAlert("CW00208", "품목 작업자 수"); 
		div_detail.ed_worker.SetFocus(); 	// 품목 작업자 수에 강제 focus처리
