	{
		gfn_showAlert("CW00208", "보유 주요 장비 현황"); 
		div_detail.ed_equipment.SetFocus(); 	// 보유 주요 장비 현황에 강제 focus처리
		return false; 
	} // [${}]은/는 필수입력 입니다. 확인바랍니다.

	if ( CAPA_CURR    == null || CAPA_CURR    == "" ) 
	{
		gfn_showAlert("CW00208", "공급 Capa/月"); 
		div_detail.ed_curr.SetFocus(); 	// 공급 Capa/月에 강제 focus처리
