/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {
	gfn_exportExcel(grd_list,G_USER_ID);
}

/********************************************************************************
* 기      능   : 삭제 
********************************************************************************/
function fn_delete() {
	if(!gfn_showConfirm( "CI00015", "해당 데이터" )) return;

	ds_list.Filter("CHECK==1");
	ds_list_0.copyF(ds_list);
	ds_list.unFilter();

	tit_clearActionInfo();
	tit_addMultiActionInfo("mm12:MM1201001A_D1");
	tit_callService(
		""
		, ""
		, "ds_list_0=ds_list_0"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {

    // 조회 결과가 있으면 엑셀 버튼 활성화
	if ( ds_list.rowCount() > 0 ) {
		ufn_enableBtn("excel", true);

		div_detail.div_gubn.btn_popSman.enable = false;
		div_detail.div_gubn.ed_sman_cd.enable = false;
		div_detail.div_gubn.ed_sman_nm.enable = false;

		div_detail.div_item.btn_popSman.enable = false;
		div_detail.div_item.ed_sman_cd.enable = false;
		div_detail.div_item.ed_sman_nm.enable = false;

		div_detail.div_sman.btn_popLifnr.enable = false;
		div_detail.div_sman.ed_lifnr.enable = false;
		div_detail.div_sman.ed_name1.enable = false;

	} else {
		ufn_enableBtn("excel", false);		
	}
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg){
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) 
{
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");	
	    fn_query();
	}	
}

//=========================================================================================
// [ PART 5 ]
// Detail Process Event 정의 ( User Define function )
// 사용자 정의 함수는 ufn_xxxx() 형태로 작성 
//=========================================================================================

//=========================================================================================
// [ PART 6 ]
// component Event 정의 : 각 Component 별 이벤트 
//=========================================================================================
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}


function fn_resize() {

	Shape0.Width = window.width - 40;
    //div_gubn.width  = "305";
    grd_list.Width = window.width - 45;
	div_detail.Width = GetPlatformInfo("WorkArea","width");
}
