/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	// 필수 입력 항목 체크 
	if ( gfn_isNull(cal_zzchakd_fm, true) ) {
		gfn_showMsg("CW00001","착공예정일(시작)");
		return;
	}
	if ( gfn_isNull(cal_zzchakd_to, true) ) {
		gfn_showMsg("CW00001","착공예정일(종료)");
		return;
	}

	if ( !gfn_checkDate( cal_zzchakd_fm.value, cal_zzchakd_to.value, true, cal_zzchakd_to) ) {
		return;
	}

	if (length(ed_posid.value) > 0 && length(ed_posid.value) < 6){
		gfn_showAlert("CW00092");
		ed_posid.SetFocus();
		return;
	}

	tit_clearActionInfo();

	tit_addSearchActionInfo("pp01:PP0102012A_S1");


	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
