/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {

	// 체크한 거래box를 새로운 dataset에 담기
	ds_list_0.ClearData();

	ds_list.fireevent = false;
	var exist_data = 0;

	//trace(ds_list.SaveXML());
	for (var i = 0 ; i < ds_list.RowCount(); i++)
	{
		 if (ds_list.GetColumn(i,"CHECK") != "1" && length(ds_list.GetColumn(i,"POSID")) >= 9)
		 {
			 ds_list.SetColumn(i,"CHECK", "1");
		 }

		if (ds_list.GetColumn(i,"CHECK") == "1")
		{ 
			ds_list.SetColumn(i,"HLDAT1", SubStr(ds_list.GetColumn(i,"HLDAT1"),0,8));
			ds_list.SetColumn(i,"HLDAT2", replace(ds_list.GetColumn(i,"HLDAT2"),".",""));
			ds_list.SetColumn(i,"HLDAT5", replace(ds_list.GetColumn(i,"HLDAT5"),".",""));
			ds_list.SetColumn(i,"HLDAT6", replace(ds_list.GetColumn(i,"HLDAT6"),".",""));
			ds_list.SetColumn(i,"HLDAT7", replace(ds_list.GetColumn(i,"HLDAT7"),".",""));
			ds_list.SetColumn(i,"HLDAT8", replace(ds_list.GetColumn(i,"HLDAT8"),".",""));
			ds_list.SetColumn(i,"HLDAT9", replace(ds_list.GetColumn(i,"HLDAT9"),".",""));
			ds_list.SetColumn(i,"SLDAT1", replace(ds_list.GetColumn(i,"SLDAT1"),".",""));
			ds_list.SetColumn(i,"SLDAT2", replace(ds_list.GetColumn(i,"SLDAT2"),".",""));
			ds_list.SetColumn(i,"SLDAT3", replace(ds_list.GetColumn(i,"SLDAT3"),".",""));
			ds_list.SetColumn(i,"SLDAT4", replace(ds_list.GetColumn(i,"SLDAT4"),".",""));
			ds_list.SetColumn(i,"SLDAT5", replace(ds_list.GetColumn(i,"SLDAT5"),".",""));
			ds_list.SetColumn(i,"SLDAT6", replace(ds_list.GetColumn(i,"SLDAT6"),".",""));
			ds_list.SetColumn(i,"SYDAT2", replace(ds_list.GetColumn(i,"SYDAT2"),".",""));
			ds_list.SetColumn(i,"SYDAT3", replace(ds_list.GetColumn(i,"SYDAT3"),".",""));
			ds_list.SetColumn(i,"SYDAT4", replace(ds_list.GetColumn(i,"SYDAT4"),".",""));
			ds_list.SetColumn(i,"SYDAT5", replace(ds_list.GetColumn(i,"SYDAT5"),".",""));
			ds_list.SetColumn(i,"SYDAT6", replace(ds_list.GetColumn(i,"SYDAT6"),".",""));
		}

		for (var j = 2; j < grd_list.GetCellCount('head'); j++)
		{
			if (length(grd_list.GetCellValue(i, j)) > 0)
			{
				exist_data++;
			}
		}
		if (length(ds_list.GetColumn(i,"POSID")) < 9 || exist_data < 1 || ds_list.GetColumn(i,"POSID") == "소계")
		{
			ds_list.SetColumn(i,"CHECK", "0");
		}

		exist_data = 0;

	}

	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_list, ds_list_0, "CHECK=1");
	//tit_showDs(ds_list_0,false);/// return;

	ds_list.fireevent = true;

	if (ds_list_0.RowCount() < 1) {
		return;
	}

	if (!gfn_showConfirm("CI00008")) {
		ds_list_0.ClearData();
		return;
	}

	tit_clearActionInfo();

	// 저장 처리 

	tit_addMultiActionInfo("pp01:PP0102012A_I1");

//   call 서비스에서 commit실행하여 commit 필요없음.

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list_0:A"
        , ""
        , ""
        , "fn_afterSave");
}
