********************************************************************************/
function fn_excel() {
	// Excel Export 
	//ds_list.Filter("CHECK == 1");
	//gfn_exportExcel(grd_list,G_USER_ID);
	var strDate = GetDate();
	strDate = Mid(strDate,0,4) + "/" + Mid(strDate,4,2) + "/" + Mid(strDate,6,2) + " " + 
	          Mid(strDate,8,2) + ":" + Mid(strDate,10,2) + ":" + Mid(strDate,12,2);

	var str = "상해제작 한국 수출품 리스트  \n"
	        + "착공예정기간 : " + cal_zzchakd_fm.value  + " ~ " + cal_zzchakd_to.value + "    "
		    + "(사양未 △, 설계未 ○, 설계完 ◎, 생산完 ⊙, 출하完 ●, 선적完 ★)"
		    +"                                                                                 "
		    +"download :"+ strDate;

	setExcelGridHeader(grd_list,str);
}

