/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {

	// 체크한 거래box를 새로운 dataset에 담기
	ds_list_0.ClearData();

	ds_list.fireevent = false;

	//trace(ds_list.SaveXML());
	for (var i = 0 ; i < ds_list.RowCount(); i++)
	{
		 if (ds_list.GetColumn(i,"CHECK") != "1" && length(ds_list.GetColumn(i,"POSID_1")) >= 9)
		 {
			 ds_list.SetColumn(i,"CHECK", "1");
		 }

		if (ds_list.GetColumn(i,"CHECK") == "1")
		{ 
			ds_list.SetColumn(i,"PODATE", replace(ds_list.GetColumn(i,"PODATE"),".",""));
			ds_list.SetColumn(i,"RSDATE", replace(ds_list.GetColumn(i,"RSDATE"),".",""));

		}


	}

	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_list, ds_list_0, "CHECK=1");
	//tit_showDs(ds_list_0,false);/// return;

	ds_list.fireevent = true;

	if (ds_list_0.RowCount() < 1) {
		return;
	}

	if (!gfn_showConfirm("CI00008")) {
		ds_list_0.ClearData();
		return;
	}

	tit_clearActionInfo();

	// 저장 처리 

	tit_addMultiActionInfo("pp01:PP0102015A_I1");

//   call 서비스에서 commit실행하여 commit 필요없음.

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list_0:A"
        , ""
        , ""
        , "fn_afterSave");
}
