/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {

    // 조회 결과가 있으면 엑셀 버튼 활성화
	if ( ds_list.rowCount() > 0 ) {
		ufn_enableBtn("excel", true);
		gfn_showMsg("CI00002", ds_list.rowCount() + "");
	} else {
		ufn_enableBtn("excel", false);
		gfn_showMsg("CW00010");
	}
}


function fn_afterSave(errCode, errMsg){
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}
