	ed_posid = hogin[0];
}

var col = "";
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);
	cal_temp.top = rect[1] +105 ;
	cal_temp.Left = rect[0]; 
	cal_temp.Visible = true;

	if (nCell = 6)
	{
		cal_temp.value = ds_list.getColumn(nRow, "PODATE");	
		col = "PODATE";
	} else if (nCell = 7)
	{
		cal_temp.value = ds_list.getColumn(nRow, "RSDATE");	
		col = "RSDATE";
	} 

	cal_temp.SetFocus();
}

/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgBar();

	ds_list.setColumn(ds_list.row, col, strText);

	col = "";
	obj.Visible = false;
}
/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우 
********************************************************************************/
function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
	col = "";
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (ds_list.GetColumn(nRow,strColumnID) == ds_list.GetOrgBuffColumn(nRow,strColumnID))
	{
		return;
	}

	ds_list.fireevent = false;

	ds_list.SetColumn(nRow,"CHECK","1");

	ds_list.fireevent = true;
}



function setExcelGridHeader(grdObj,strText)
{
	create("grid","grdExcel",'BindDataSet="' + grdObj.BindDataset +'"');
	grdExcel.contents = grdObj.Contents;

	var headCnt = getHeadCount(grdExcel);
	var totCol  = grdExcel.GetColCount();

	var irow;

	for(var i = 0 ; i < headCnt ; i++ )
	{
		irow = toInteger(grdExcel.GetCellProp("head",i,"row"));
		grdExcel.SetCellProp("head",i,"row",irow+1);
	}

	var str_temp = grdExcel.GetCurContents();
	var str_Head = '<cell align="left" col="0" colspan="' + totCol + '" display="text" text="' + strText + '"/>';	
	str_temp = replace(str_temp, '</head>',str_Head +'</head>');

	grdExcel.contents = str_temp;

	grdExcel.ExportExcelEx(cal_recad_fm.value  + "~" + cal_recad_to.value,"A1",false,true,true);

	Destroy(grdExcel);
}

function getHeadCount(grdObj)
{
	var strConts = grdObj.Contents;

	var iPos = pos(strConts,"<head>");

	var strHead = Mid(strConts,iPos);

	iPos = pos(strHead,"</head>");

	strHead = Mid(strHead,0,iPos+7);

//	trace(strHead);

	var spObj = split(strHead,chr(10),"webstyle");

	return spObj.length()-2;
}


function bt_exup_OnClick(obj)
{
	ds_list.ClearData();
	gfn_importExcel(grd_list, false);
}



/********************************************************************************
* 기      능   : 리스트의 폰트 색깔 처리 
********************************************************************************/
function ufn_setColor(obj,DSTAT)
{
	cValue = decode(DSTAT,'●','red','▲','yellow','■','green','default');
	// 선택된 로우의 Font Color
	obj.SelColor = cValue;
	// Grid의 Font Color
	return cValue;
}


// 클릭별 데이터 입력 처리

function grd_list_OnCellDBClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var arg = "fa_posid=" + quote(ds_list.GetColumn(nRow,"POSID"));

	if (nCell = 3) {
		gfn_openPopup("PP0102015B", true, arg);
	} else if (nCell = 11) {
		gfn_openPopup("PP0102015C", true, arg);
	} else if (nCell = 13) {
		gfn_openPopup("PP0102015E", true, arg);
	} else if (nCell = 18) {
		gfn_openPopup("PP0102015F", true, arg);
	}
}
