// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg)
{

	gfn_showMsg("CI00002",ds_head.RowCount()+"");

	if(ds_head.RowCount() > 0)
	{	
		//trace('g_addr is not empty? ' + Length(ds_head.GetColumn(0, 'G_ADDR')));
		if (Length(Trim(ds_head.GetColumn(0, 'G_ADDR'))) > 0) {
			btnSetMap.Visible = true;
			btnCpMapUrl.Visible = true;
			btnGoUrl.Visible = true;
		} else {
			//btnSetMap.Visible = false;
			//btnCpMapUrl.Visible = false;
			//btnGoUrl.Visible = false;
		}
		G_MEMO.Readonly = false;

		fn_query1();
		fn_query2();

		if(ds_head.GetColumn(0, "VIPTP") == 'Yes')
		{
			gfn_openMsgWin("VIP대상 Project입니다.");
		}

		// 업체정보 저장버튼
		btn_tabSave.Enable = true;

		PUSER = "";

		// 생산관리담당자 추가
		tit_clearActionInfo();
		tit_callService(
          "PS0101001A_ACT"
        , "getPrdUser"
        , "ds_cond=ds_cond"
        , ""
        , ""
        , "fn_afterQueryPrd"
        , true);
	}
	else
	{
		init = false;
		fv_end1 = true;
		fv_end2 = true;
		fv_end3 = true;

		// 업체정보 저장버튼
		btn_tabSave.Enable = false;
	}
}
