function fn_afterQuery2(errCode, errMsg)
{
	gfn_showMsg("CI00002",ds_list2.RowCount()+"");


	//=======인계파일탭 데이타 가져오기=================
	ds_list2.Filter("ZZGUBUN==10 OR ZZGUBUN==11 OR ZZGUBUN==12 OR ZZGUBUN==13 OR ZZGUBUN==14");
	ds_file.copyF(ds_list2);
	ds_list2.unFilter();
	//==================================================

	if(ds_list3.rowcount > 0) filterList();

	fv_end3 = true;
	init = false;

	fn_afterQuery3();
	//Trace("3");
}

function fn_afterQuery3() {
	var PSPID = ds_cond.GetColumn(0, 'POSID');

	ds_list10_tmp.ClearData();

	ds_list10_tmp.addRow();
	ds_list10_tmp.SetColumn(0, 'POSID', PSPID);
	ds_list10_tmp.SetColumn(0, 'GUBUN', '도면');
	ds_list10_tmp.SetColumn(0, 'CATE', 'L');
	ds_list10_tmp.SetColumn(0, 'UDATE', ds_list10.GetColumn(0, 'UDATE'));
	ds_list10_tmp.SetColumn(0, 'UDATE2', ds_list10.GetColumn(0, 'UDATE2'));
	ds_list10_tmp.SetColumn(0, 'BIGO', ds_list10.GetColumn(0, 'BIGO'));
	ds_list10_tmp.SetColumn(0, 'FNAME', PSPID + '.pdf');

	tit_callService(
          "PS0101001A_ACT"
        , "execute"
        , "ds_cond=ds_cond"
        , "ds_pdm=ds_pdm"
        , ""
        , ""
        , true
        , true
        , true);

	for (var i = 0; i < ds_pdm.RowCount(); i++) {
		var CATE = ds_pdm.GetColumn(i, 'CATE');

		if (!(CATE == 'D' || CATE == 'R')) {
			continue;
		}

		var path = ds_pdm.GetColumn(i, 'PATH');
		var ext = substr(path, ufn_lastIndexOf(path));
		var cate = ds_pdm.GetColumn(i, 'CATE');
		var cdate = ds_pdm.GetColumn(i, 'CDATE');
		cdate = Mid(cdate, 0, 4) + "-" + Mid(cdate, 4, 2) + "-" + Mid(cdate, 6, 2) + " " + Mid(cdate, 8, 2) + ":" + Mid(cdate, 10, 2) + ":" + Mid(cdate, 12, 2);

		var GUBUN = '';
		if (cate == 'D') {
			GUBUN = '시방서';
		} else if (cate == 'L') {
			GUBUN = '도면';
		} else if (cate == 'R') {
			GUBUN = '렌더링';
		}

		ds_list10_tmp.addRow();

		ds_list10_tmp.SetColumn(i + 1, 'GUBUN', GUBUN);
		ds_list10_tmp.SetColumn(i + 1, 'CATE', cate);
		ds_list10_tmp.SetColumn(i + 1, 'FNAME', PSPID + '-' + cate + '.' + ext);
		ds_list10_tmp.SetColumn(i + 1, 'FPATH', path);
		ds_list10_tmp.SetColumn(i + 1, 'UDATE2', cdate);

	}
	ds_list10.copyF(ds_list10_tmp);

	// 호기정보 GATE 정보 가져오기

	ds_cond_gate.ClearData();

	for(var i=0;i<ds_list2.GetRowCount();i++)
	{
		var nAddRow = ds_cond_gate.AddRow();
		ds_cond_gate.SetColumn(nAddRow, "POSID", ds_list2.GetColumn(i, "POSID_1"));
		ds_cond_gate.SetColumn(nAddRow, "GATE", "");
	}

	if(ds_cond_gate.getRowCount() > 0)
	{
		tit_callService(
          "PS0101001A_ACT"
        , "getGateInfo"
        , "ds_cond=ds_cond_gate"
        , "ds_list_gate=ds_list"
        , ""
        , ""
        , true
        , true
        , true);


        for(var i=0;i<ds_list_gate.GetRowCount();i++)
        {
			var nSearchRow = ds_list2.SearchRow("POSID_1 == '"+ds_list_gate.GetColumn(i, "POSID")+"'");

			if(nSearchRow >= 0)
			{
				ds_list2.SetColumn(nSearchRow, "GATE", ds_list_gate.GetColumn(i, "GATE"));
			}

        }
	}




}

