function fn_excel()
{
	if( ds_head.rowcount > 0 )
	{
		switch (Tab00.TabIndex)
		{
			case 1 :gfn_saveToExcel(Tab00.tab2.grd_list2, true );
					break;
			case 2 :gfn_saveToExcel(Tab00.tab3.grd_list2, true );
					break;
			case 3 :gfn_saveToExcel(Tab00.tab4.grd_list2, true );
					break;

			case 4 :gfn_saveToExcel(Tab00.tab5.grd_list7, true );
					break;
			case 5 :gfn_saveToExcel(Tab00.tab6.grd_list2, true );
					break;

		}
	}
}

/********************************************************************************
* 기      능   : print 40
********************************************************************************/
function fn_print()
{
	ds_head_p.DeleteAll();
	ds_head_p.AddRow();

	ds_head_p.copyf(ds_head);

	ds_head_p.SetColumn(0,"VDATU",gfn_formatDate(ds_head_p.GetColumn(0,"VDATU"),'.'));
	ds_head_p.SetColumn(0,"CONTR_DA",gfn_formatDate(ds_head_p.GetColumn(0,"CONTR_DA"),'.'));
	ds_head_p.SetColumn(0,"CDATE",gfn_formatDate(ds_head_p.GetColumn(0,"CDATE"),'.'));

	if (ds_head_p.rowcount < 1)
	{
		gfn_showAlert("CW00013");
		return;
	}

	var arg;
	if( Tab00.TabIndex == 0 )
	{
		arg = "arg=0";
		ds_list1_p.DeleteAll();
		ds_list1_p.AddRow();
		ds_list1_p.copyf(ds_list1);
	}
	else if( Tab00.TabIndex == 1 )
	{
		gfn_openPopup("PS0101001D",true);
		return;
	}
	else if( Tab00.TabIndex == 2 || Tab00.TabIndex == 3)
	{
		return;
		arg = "arg=2";
		ds_list3_p.DeleteAll();
		ds_list3_p.AddRow();
		ds_list3_p.copyf(ds_list3);
		for( var i = 0; i < ds_list3_p.RowCount(); i++ )
		{
			ds_list3_p.SetColumn(i,"NO",i+1);
			ds_list3_p.SetColumn(i,"CR_DATE",gfn_formatDate(ds_list3_p.GetColumn(i,"CR_DATE"),'.'));
			ds_list3_p.SetColumn(i,"TP_DATE",gfn_formatDate(ds_list3_p.GetColumn(i,"TP_DATE"),'.'));
			ds_list3_p.SetColumn(i,"CR_TIME",gfn_formatDate(ds_list3_p.GetColumn(i,"CR_TIME"),'.'));
			ds_list3_p.SetColumn(i,"TP_TIME",gfn_formatDate(ds_list3_p.GetColumn(i,"TP_TIME"),'.'));
		}
	}

	// UbiReport Call
	Dialog("PS01::PS0101001P.xml", arg);
}

/********************************************************************************
* 기      능   : print 40
********************************************************************************/
function fn_print1()
{
	var arg = args;
	ds_list2_p.DeleteAll();
	ds_list2_p.AddRow();

	ds_list2.Filter("Substr(POSID_1,6,1) <> 'Y'");
	ds_list2_p.copyf(ds_list2);
	ds_list2.UnFilter();

	for( var i = 0; i < ds_list2_p.RowCount(); i++ )
	{
		ds_list2_p.SetColumn(i,"NO",i+1);
		ds_list2_p.SetColumn(i,"VDATU",gfn_formatDate(ds_list2_p.GetColumn(i,"VDATU"),'.'));
		ds_list2_p.SetColumn(i,"ZZCHAKD",gfn_formatDate(ds_list2_p.GetColumn(i,"ZZCHAKD"),'.'));
		ds_list2_p.SetColumn(i,"ZZCOMP2",gfn_formatDate(ds_list2_p.GetColumn(i,"ZZCOMP2"),'.'));
		ds_list2_p.SetColumn(i,"ZZPMJID",gfn_formatDate(ds_list2_p.GetColumn(i,"ZZPMJID"),'.'));
		ds_list2_p.SetColumn(i,"ZZDAT01",gfn_formatDate(ds_list2_p.GetColumn(i,"ZZDAT01"),'.'));
		ds_list2_p.SetColumn(i,"ZQEGEMSA",gfn_formatDate(ds_list2_p.GetColumn(i,"ZQEGEMSA"),'.'));
		ds_list2_p.SetColumn(i,"ZBEPJEO",gfn_formatDate(ds_list2_p.GetColumn(i,"ZBEPJEO"),'.'));
		ds_list2_p.SetColumn(i,"ZBOSUOUT",gfn_formatDate(ds_list2_p.GetColumn(i,"ZBOSUOUT"),'.'));
		ds_list2_p.SetColumn(i,"ZBOSUIN",gfn_formatDate(ds_list2_p.GetColumn(i,"ZBOSUIN"),'.'));
		ds_list2_p.SetColumn(i,"NAMET2",replace(ds_list2_p.GetColumn(i,"NAMET2")," ",""));

		//옵션사항
		var text1 = ds_list2_p.GetColumn(i,"TEXT1");
		var text2 = ds_list2_p.GetColumn(i,"TEXT2");
		for(var j=0; j<ds_list5.RowCount(); j++)
		{
			if(ds_list2_p.GetColumn(i,"POSID_1") == ds_list5.GetColumn(j,"HOGI"))
			{
				text1 = text1 + '\n ' + ds_list5.GetColumn(j,"TEXT1");
				text2 = text2 + '\n ' + ds_list5.GetColumn(j,"TEXT2");
			}
		}
		//기계담당자,전기담당자
		for(var j=0; j<ds_list6.RowCount(); j++)
		{
			if(ds_list2_p.GetColumn(i,"POSID_1") == ds_list6.GetColumn(j,"POSID_1"))
			{
				ds_list2_p.SetColumn(i,"KUSER",ds_list6.GetColumn(j,"KUSER"));
				ds_list2_p.SetColumn(i,"JUSER",ds_list6.GetColumn(j,"JUSER"));
				ds_list2_p.SetColumn(i,"KTELH",ds_list6.GetColumn(j,"KTELH"));
				ds_list2_p.SetColumn(i,"JTELH",ds_list6.GetColumn(j,"JTELH"));
			}
		}
		ds_list2_p.SetColumn(i,"TEXT1",text1);
		ds_list2_p.SetColumn(i,"TEXT2",text2);
	}

	// UbiReport Call
	Dialog("PS01::PS0101001P.xml", arg);
}

