* 기      능   : 도면다운로드
********************************************************************************/
function Tab00_tab8_btn_down_OnClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if ( length(ds_list10.getColumn(nRow, "UDATE")) > 0) {
		fn_createWait("", this);
		var FileName = ds_list10.getColumn(nRow, "POSID") +'.pdf';

		saveDialog.FileName = FileName;	//tit_getLocalFileName(ds_list2.getColumn(nRow, "FNAME"));

		if (saveDialog.open()) {
			tit_fileRead(
				httpFile
				, "PDM"
				, FileName
				, saveDialog.FilePath
				, saveDialog.FileName);
		}
		fn_destroyWait();
	} else {
		gfn_alert("해당 프로젝트는 PDF 도면이 없습니다."
				 + "\n"
				 + "\n(PDM에서 조회하시기 바랍니다)");
    }

}

