function Tab00_tab8_grd_sibang_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var cate = ds_list10.GetColumn(nRow, 'CATE');
	if (cate == 'L' && nCell == 1) {
		Tab00_tab8_btn_pdf_OnClick(obj,nRow,nCell,nX,nY,nPivotIndex);
	} else if (cate == 'L' && nCell == 2) {
		Tab00_tab8_btn_down_OnClick(obj,nRow,nCell,nX,nY,nPivotIndex);
	} else {
		if (nCell == 1) {
			fn_open_file(obj,nRow,nCell,nX,nY,nPivotIndex);
		} else if (nCell == 2) {
			fn_download_file(obj,nRow,nCell,nX,nY,nPivotIndex);
		}
	}
}

function fn_open_file(obj,nRow,nCell,nX,nY,nPivotIndex) {
	fn_createWait("", this);

	var FileName = ds_list10.GetColumn(nRow, "FNAME");
	var FPATH = ds_list10.GetColumn(nRow, "FPATH");

	//trace('[' + ds_list10.RowCount() + ' : ' + nRow + ']file name : ' + FileName);
	//trace('[' + ds_list10.RowCount() + ' : ' + nRow + ']file path : ' + FPATH);

	if (Length(FPATH) == 0 && Length(FileName) == 0) {
		gfn_showAlert("CI30001", "파일이 존재하지 않습니다.");
		return;
	}

	var FilePath = "c:\\Temp" ;
	var FileExec = "c:\\Temp\\" + FileName;

	var rMsg = tit_fileReadFromFtpServer(
		httpFile
		, FPATH  	  // 저장 디렉토리
		, FileName	  // 서버에서 전송받을 이름
		, FilePath    // 로컬 경로
		, FileName    // 로컬 파일명
		);

	fn_destroyWait();

	if ( rMsg[0] == "E" ) {
		if ( length(trim(rMsg[1])) > 0 ) {
			gfn_showAlert("CW10025", rMsg[1]);
		} else {
			gfn_showAlert("CW10025", "파일 다운로드에 실패하였습니다.");
		}
	} else {
		ExecShell(FileExec);
	}
}

function fn_download_file(obj,nRow,nCell,nX,nY,nPivotIndex) {
	fn_createWait("", this);

	var FileName = ds_list10.getColumn(nRow, "FNAME");
	var FPATH = ds_list10.getColumn(nRow, "FPATH");

	if (Length(FPATH) == 0 && Length(FileName) == 0) {
		gfn_showAlert("CI30001", "파일이 존재하지 않습니다.");
		return;
	}


	saveDialog.FileName = FileName;

	if (saveDialog.open()) {
		tit_fileReadFromFtpServer(
			httpFile
			, FPATH
			, FileName
			, saveDialog.FilePath
			, saveDialog.FileName
		);
	}

	fn_destroyWait();
}

function fn_getDir(cate) {
	var dir = '';
	if (cate == 'L') {
		dir = '/interface/layout/' + posid + '/';
	} else if (cate == 'R') {
		dir = '/interface/rendering/' + posid + '/';
	} else if (cate == 'D') {
		dir = '/interface/specify/' + posid + '/';
	}
	return dir;
}


