function ufn_lastIndexOf(sParmString)
{
	var j = 0;//.위치
	var k = 0;// 결과위치
	var k = 0;//.위치 -1


	while(true)
	{
		r = pos(sParmString,".",j);

		if (r == -1)
		{
			break;
		}

		j=r+1;

	}
	k = j;

	return k;
}

