function btn_tabSave_OnClick(obj) {

	for(var i=0; i<ds_list09.GetRowCount(); i++){
		if(ds_list09.GetColumn(i, "CHK")==1){
			// 필수 입력 항목			
			if(length(ds_list09.GetColumn(i, "L_TELE"))<=0 ){
				alert((i+1)+"행의 제작업체 연락처 정보를 입력해 주세요.");
				gfn_showMsg('CW00017', ToString(i+1));
				return;			
			}			
			if(length(ds_list09.GetColumn(i, "L_EMAIL"))<=0 ) {
				alert((i+1)+"행의 제작업체 이메일 정보를 입력해 주세요.");
				gfn_showMsg('CW00017', ToString(i+1));
				return;
			}
		}
	}

	fn_saveMMlifnr();
}

