function fn_OnLoadCompleted(obj) 
{
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	user_grp = gfn_getUserGroup();

	//대산엘리베이터,윤송이엔씨..등의 외주업체는 'E40'권한으로 조회하도록 예외처리(20090304:한상협CJ)
	if( G_VEND_CODE_E == '1068643714' || G_VEND_CODE_E == '1178148960' || G_VEND_CODE_E == '1198601821' || G_VEND_CODE_E == '5068192953' || G_VEND_CODE_E == '1381306973' || G_VEND_CODE_E == '2098134940' || G_VEND_CODE_E == '2062376067'|| G_VEND_CODE_E == '2098160036'||G_VEND_CODE_E == '1148679004'||G_VEND_CODE_E == '1328604777' || G_VEND_CODE_E == '3160128045' || G_VEND_CODE_E == '2240259533')//엔아이피추가 + 알엔비시스템(계원찬 sw요청)
	{
		user_grp = '40';
	}

	ed_pjt.SetFocus();

	if ( isExistVar("posid", true))
	{
		ed_pjt.Text = posid;
		fn_query();
	}
}
