/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() 
{
	//년필드 필수체크	
	if(gfn_isNull(ed_pjt, true, st_pjt.value))
		return;

	if(user_grp == '10') 
	{
		ds_cond.SetColumn(0,"GBN","10");

		//=협력업체는 계약형태 및 계약완료여부 보여지지 않게 수정=//
		Edit9.Visible = false;
		Edit5.Visible = false;
		//========================================================//
	} 
	else if(user_grp == '30' || user_grp == '40') 
	{
		ds_cond.SetColumn(0,"GBN","");
	}


	tit_clearActionInfo();

	//tit_addSearchActionInfo("ps01:PS0101001A_S1");  //==> CsvSaerch로 변경
	tit_addCsvSearchActionInfo("ps01:PS0101001A_S1");  

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_head=ds_head"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 조회(특기사항)
********************************************************************************/
function fn_query1() 
{
	ds_cond1.DeleteAll();
	ds_cond1.AddRow();
	ds_cond1.SetColumn(0,"PSPID",ds_head.GetColumn(0,"POSID"));

	//로그인사용자가 영업담당자,같은 대리점인 경우 수금정보탭에서 금액 보여줌, 아닌경우 금액 숨김
	//홍만유CJ,신명호SW인 경우 금액 보여줌
	if(G_USER_ID == right(ds_head.GetColumn(0,"ZMAN"),7) || G_VEND_CODE_E = ds_head.GetColumn(0,"ZLIFNR")
	   || G_USER_ID =='2016893' || G_USER_ID == '2004968')
	{
		Tab00.tab6.grd_list2.Format = "Default";
		Tab00.tab6.md_netwr.Visible = true;
		Tab00.tab6.st_netwr.Visible = true;
		ds_cond1.SetColumn(0,"ZMANYN","Y");
	}	
	else
	{
		Tab00.tab6.grd_list2.Format = "Default0";
		Tab00.tab6.md_netwr.Visible = false;
		Tab00.tab6.st_netwr.Visible = false;
		ds_cond1.SetColumn(0,"ZMANYN","N");
	}	

	tit_clearActionInfo();

	tit_callService(
          "PS0101001A_ACT"
        , "query"
        , "ds_cond=ds_cond1"
        , "ds_list1=ds_list"
        , ""
        , "fn_afterQuery1"
        , false);


	tit_callService(
          "PS0101001A_ACT"
        , "query2"
        , "ds_cond=ds_cond1"
        , "ds_list8=ds_list1 ds_list9=ds_list2"
        , ""
        , "fn_afterQuery1"
        , false);
}
/********************************************************************************
* 기      능   : 조회(호기정보)
********************************************************************************/
function fn_query2() 
{
	ds_cond2.DeleteAll();
	ds_cond2.AddRow();
	ds_cond2.SetColumn(0,"POSID",ds_head.GetColumn(0,"POSID"));

    //자재협력사,영업대리점은 모든업체 호기정보 조회하도록 수정 : 2008.10.27
	if(user_grp == '10' && trim(G_USER_GROUP_M) != 'M10' && trim(G_USER_GROUP_B) != 'B10' && trim(G_USER_GROUP_R) != 'R10')
	{
		ds_cond2.SetColumn(0,"GBN","10");
	}
	else if(user_grp == '10' || user_grp == '30' || user_grp == '40')
	{
		ds_cond2.SetColumn(0,"GBN","");
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps01:PS0101001A_S2");
	tit_addSearchActionInfo("ps01:PS0101001A_S3");
	tit_addSearchActionInfo("ps01:PS0101001A_S4");
	tit_addSearchActionInfo("ps01:PS0101001A_S5");
	tit_addSearchActionInfo("ps01:PS0101001A_S6");
	tit_addSearchActionInfo("ps01:PS0101001A_S7");
	tit_addSearchActionInfo("ps01:PS0101001A_S8");
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_list2=ds_list2 ds_list3=ds_list3 ds_list4=ds_list4 ds_list5=ds_list5 ds_list6=ds_list6 ds_list7=ds_list7 ds_list10=ds_list10"
        , ""
        , "fn_afterQuery2"
        , true);

}
