/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery2(errCode, errMsg) 
{
	gfn_showMsg("CI00002",ds_list2.RowCount()+"");

	//=======인계파일탭 데이타 가져오기=================
	ds_list2.Filter("ZZGUBUN==10 OR ZZGUBUN==11 OR ZZGUBUN==12 OR ZZGUBUN==13 OR ZZGUBUN==14");
	ds_file.copyF(ds_list2);
	ds_list2.unFilter();
	//==================================================

	if(ds_list3.rowcount > 0) filterList();
}
