* 기      능   : 리스트의 타이틀 클릭  
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) 
	{
		switch (Tab00.TabIndex) 
		{
			case 1 :gfn_selectGridCol("Tab00.tab2.grd_list2",true);
					break;
			case 2 :gfn_selectGridCol("Tab00.tab3.grd_list2",true);
					break;
			case 3 :gfn_selectGridCol("Tab00.tab4.grd_list2",true);
					break;			
		}
	} 
	else if ( nCell > 0 ) 
	{
		gfn_sortGrid(obj, nCell);
	}	
}
