/********************************************************************************
* 기      능   : 검사 성적서 팝업
********************************************************************************/
function Tab00_tab2_grd_list2_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	// 성적서 유형 (완료)
	if( nCell==25 ) //&& ( ds_list2.GetColumn(nRow, "DSCODE") == '합격' || ds_list2.GetColumn(nRow, "DSCODE") == '불합격' ) )
	{
		var gbn = substr(ds_list2.GetColumn(nRow, "POSID_1"), 6, 1);
		var pjt = substr(ds_list2.GetColumn(nRow, "POSID_1"), 0, 6);
		var hno = ds_list2.GetColumn(nRow, "POSID_1");


		//fn_goNextForm(gbn,pjt,hno);
		fn_choicdForm(gbn,pjt,hno);
	}		
}

//검사성적서 재개발
function fn_choicdForm(gbn,pjt,hno)
{
	var arg = "POP_GBN="+quote(gbn);
	    arg += " POP_PJT="+quote(pjt);
	    arg += " POP_HNO="+quote(hno);

	gfn_openPopup("PS0102001C",true,arg);
}


function fn_goNextForm(gbn,pjt,hno){
	var formID="";

	switch (gbn) {

		case 'L':
			formID = "PS0101001L";
			break;			

		case 'S':
			formID = "PS0101001S";
			break;

		case 'W':
			formID = "PS0101001SW";
			break;	

	}

	var arg = "PRE_PJT="+quote(pjt);
		arg += " PRE_HNO="+quote(hno);

	//SELFLG = "N";
	gfn_openPopup(formID, true, arg);
	//if(SELFLG == "Y") fn_query();
}


