/********************************************************************************
* 기      능   : 도면열기
********************************************************************************/
function Tab00_tab8_btn_pdf_OnClick(obj)
{
	  if ( length(ds_list10.getColumn(0, "UDATE")) > 0) {
	  fn_createWait("", this);	
		    //alert("a");				
			//==========================================================================================================
			//var FileName = ds_list.getColumn(nRow, "FNAME");//tit_getLocalFileName(ds_list2.getColumn(nRow, "FNAME"));	
			var FileName = ds_list10.getColumn(0, "POSID") +'.pdf';
			var FilePath = "c:\\Temp" ;		
			var FileExec = "c:\\Temp\\" + FileName;	


			var rMsg = tit_fileRead(
									httpFile
									, "PDM"  	  // 저장 디렉토리
									, FileName	// 서버에서 전송받을 이름
									, FilePath    // 로컬 경로
									, FileName    // 로컬 파일명
								   );

			//alert("b");
			fn_destroyWait();
			if ( rMsg[0] == "E" ) {
				if ( length(trim(rMsg[1])) > 0 ) {

					gfn_showAlert("CW10025", rMsg[1]);
				} else {

					gfn_showAlert("CW10025", "파일 다운로드에 실패하였습니다.");
				}
			} else {
			  // alert("c");
				//gfn_showAlert("CW10025", FilePath + " 위치에 파일 저장됨.");		
				ExecShell(FileExec);		
			}								
			//==========================================================================================================		

		}
	 else

      {
           gfn_alert("해당 프로젝트는 PDF 도면이 없습니다."
                     + "\n"
                     + "\n(PDM에서 조회하시기 바랍니다)");          
      }
}

