/********************************************************************************
* 기      능   : 최신현장전화 수정
********************************************************************************/
function btn_modify_OnClick(obj)
{
	var sPosId = ds_head.getColumn(0, "POSID");

	if(sPosId == "" || length(sPosId) == 0) return;

	//# 현장 전화번호 저장
	tit_clearActionInfo();		
	tit_addSelectExActionInfo("ps01:PS0101001A_S9", "ps01:PS0101001A_I1", "ps01:PS0101001A_U1");	// ZPSTPM07 INSERT OR UPDATE.
	tit_callService(
		""
		, ""
		, "ds_head=ds_head"
		, ""
		, ""
		, "fn_SaveTel"
		, ""
		, ""
		, true  // ""

		);
}

/********************************************************************************
* 기      능   : 최신현장전화 수정 후 Callback
********************************************************************************/
function fn_SaveTel()
{
	if ( errCode!= 0 ) {
	//  CE00001 : "처리에 실패했습니다.\n확인 후 다시 처리해 주십시오.");'
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

/********************************************************************************
* 기      능   : 최신현장전화 입력 시 숫자만 입력가능하도록
********************************************************************************/
function Edit20_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
	// 숫자와 - 만 입력가능하도록 설정
	// 8 : backspace 키, 46 : Delete 키, 45 : -, 나머지 : 숫자 키
	if(!(nChar>= 48 && nChar <= 57) && nChar != 45 && nChar != 46 && nChar != 8)
	{
		return false;
	}
}
