var strId ="";
var userGroup = "";
var fv_today = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	// 날짜조건 자동 세팅 2019-04-25 계원찬SW 요청	
	fv_today          = gfn_getCurrDate();
	cal_fdate.value   = gfn_getBeforeDate(fv_today,365); //substr(fv_today,0,6);
    cal_tdate.value   = fv_today; 

	userGroup = gfn_getUserGroup();
	//설치부 특정직원만 수주금액으로 조회할수 있도록 처리 (20090414 - 한상협cj)
	if( userGroup == '40' && (G_USER_ID == '2019302' || G_USER_ID == '2008157' || G_USER_ID == '2016940' || 
	                          G_USER_ID == '3018598' || G_USER_ID == '2018578' || G_USER_ID == '3018903' ||
	                          G_USER_ID == '2021757' || G_USER_ID == '2022702' || G_USER_ID == '2026490') ) // 2016.01.08 박한님 SW 추가 
	{
		med_amtf.value = '';
		med_amtt.value = '';
		med_amtf.Enable = true;
		med_amtt.Enable = true;
	}
	else
	{
		med_amtf.value = '';
		med_amtt.value = '';
		med_amtf.Enable = false;
		med_amtt.Enable = false;
	}

	var codeArr = "";
	// 보수대지역 콤보 공통코드	
	if(length(G_USER_GROUP_E) > 0)
	{
		codeArr = ["PS003"];
		ds_cond.SetColumn(0,"GBN","E");
		if(userGroup == '10')
			ds_cond.SetColumn(0,"GBN","E10");
	}
	else if(length(G_USER_GROUP_R) > 0)
	{
		codeArr = ["CS115"];
		ds_cond.SetColumn(0,"GBN","R");
		if(userGroup == '10')
			ds_cond.SetColumn(0,"GBN","R10");
	}
	else
	{
		codeArr = ["PS003"];
		ds_cond.SetColumn(0,"GBN","E");
		if(userGroup == '10')
			ds_cond.SetColumn(0,"GBN","E10");
	}
	var dsObjArr = [ds_zzactss];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	if(G_USER_ID == '5007909')   //인천 이혜원 전체조회권한 추가(강제혁cj요청)
	{ 
	    userGroup = '40';
	}
	//cal_fdate.Value = substr(gfn_getCurrDate(),0,6)+"01";
	//cal_tdate.Value = gfn_getCurrDate();

}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_zzactss.value = "";	
	cb_gubun.Index = 0;
	if(userGroup == '40')
	{
		cb_zzactss.value = "";
	}
	else if(userGroup == '20' || userGroup == '30')
	{
		cb_zzactss.Value = G_AREA_CODE_E;
		if(G_USER_GROUP_E == 'E20' || G_USER_GROUP_E == 'E30')
		{
			cb_zzactss.Enable = true;
		}	
	}
	else
	{
		cb_zzactss.value = "";
		cb_zzactss.Enable = true;
	}
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
