/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {	
	gfn_clearGridSortMark(grd_list);

	// 프로젝트를 조회조건으로 입력하지 않을 경우
	// 지역, PM, 일자 입력 필수


	if(ed_pjt.Value == '' || length(ed_pjt.Value) == 0)
	{
		/*
		if( ((cb_zzactss.Value == '' || length(cb_zzactss.Value) == 0) || (cb_combo2.Value == '' || length(cb_combo2.Value) == 0) 
		    || (cal_fdate.Value == '' || length(cal_fdate.Value) == 0) || (cal_tdate.Value == '' || length(cal_tdate.Value) == 0) 
		    )
		    && (ed_comNm.Value == '' || length(ed_comNm.Value) == 0)
		    && (ed_siteNm.Value == '' || length(ed_siteNm.Value) == 0)

		  )
		{
			gfn_openMsgWin("Proj No를 입력하지 않은 경우\n[지역, PM, 일자] 또는 [현장명/건설업체명]은\n필수입력사항입니다.");
			return;
		}
		*/

		if((ds_cond.getColumn(0, "GUBUN") == '' || length(ds_cond.getColumn(0, "GUBUN")) == 0) || (cal_fdate.Value == '' || length(cal_fdate.Value) == 0) || (cal_tdate.Value == '' || length(cal_tdate.Value) == 0) )
		{
			gfn_openMsgWin("일자를 선택하세요.(From일자 ~ To일자)");
			return;
		}
		else
		{
			gfn_getDayCnt(ds_dayCnt, cal_fdate.Value, cal_tdate.Value);
			nDays = ds_dayCnt.GetColumn(0, "DAYS");

			if(nDays > 365)
			{
				gfn_openMsgWin("조회 범위는 1년이내로 제한됩니다.");
				cal_fdate.Value = gfn_getAfterDate(gfn_getCurrDate(),-365);
				cal_tdate.Value = gfn_getCurrDate();
				return;
			}
		}




	}


	if ( !gfn_checkFromToObj(cal_fdate, cal_tdate, true, st_date.value) ) {
		return;
	}

	if ( med_amtt.Value != '' && int(med_amtf.Value) > int(med_amtt.Value) ) 
	{    
	    alert(med_amtt.Value);
		gfn_showMsg("CW00012");
		return;
	}
	if ( med_amtf.Value > 0 && med_amtt.Value == '' ) 
	{
		ds_cond.SetColumn(0,"GBN2","A");
	}
	else if ( (med_amtf.Value == '' || med_amtf.Value == 0) && med_amtt.Value > 0 ) 
	{
		ds_cond.SetColumn(0,"GBN2","B");
	}
	else if ( med_amtf.Value > 0 && med_amtt.Value > 0 ) 
	{
		ds_cond.SetColumn(0,"GBN2","C");
	}
	else ds_cond.SetColumn(0,"GBN2","N");

	if(ds_cond.GetColumn(0,"GUBUN") == "1" && cal_fdate.Value > '' && cal_tdate.Value > '')
		ds_cond.SetColumn(0,"GUBUN","1");
	else if(ds_cond.GetColumn(0,"GUBUN") == "2" && cal_fdate.Value > '' && cal_tdate.Value > '')
		ds_cond.SetColumn(0,"GUBUN","2");
	else if(ds_cond.GetColumn(0,"GUBUN") == "3" && cal_fdate.Value > '' && cal_tdate.Value > '')
		ds_cond.SetColumn(0,"GUBUN","3");
	else if(ds_cond.GetColumn(0,"GUBUN") == "4" && cal_fdate.Value > '' && cal_tdate.Value > '')
		ds_cond.SetColumn(0,"GUBUN","4");
	else if(ds_cond.GetColumn(0,"GUBUN") == "5" && cal_fdate.Value > '' && cal_tdate.Value > '')
		ds_cond.SetColumn(0,"GUBUN","5");
	else
		ds_cond.SetColumn(0,"GUBUN","");

	//E/S 외장공사 관련 추가
	if(ck_es.Value == true)
	{
		ds_cond.SetColumn(0,"CHK_ES","Y");
	}
	else
	{
		ds_cond.SetColumn(0,"CHK_ES","");
	}

	if(G_USER_ID == '2029847')
	{
			 File0.FileName = "C:\\Users\\User\\Documents\\test.ini";          // C:\Users\User\Documents 폴더에 test.ini 생성해 둠
			 if( File0.open("a+") )                  {                               
						var len = File0.write("START :  "+getDate() + (Timem() - toFloat(Time()+"000"))+"\n",-1,"utf-8");                            
						File0.close();
			 }

	}

//trace(ds_cond.SaveXML());
//alert(ds_cond.GetColumn(0,"CHK_ES"));

	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps01:PS0101002A_S1"); //==> CsvSaerch로 변경
	tit_addCsvSearchActionInfo("ps01:PS0101002A_S1"); 

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
