	ds_cond.SetColumn(0,"POSID","");
	ds_cond.SetColumn(0,"ZSITE_NM","");
	ds_cond.SetColumn(0,"KUNNR_NM","");
	ds_cond.SetColumn(0,"ZMAN_NM","");
}
