/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	// 그리드 정렬 처리 
	gfn_sortGrid(obj,nCell);
}

function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var arg = "posid="+quote(ds_list.GetColumn(nRow,"POSID"));
	gfn_openPopup("PS0101002B",true,arg);

	if(Length(ed_pjt.Text) > 6)
	{
		gfn_openMsgWin("프로젝트 번호는 6자리까지만\n입력 가능합니다.");
		ed_pjt.Text = "";
		return;
	}

}
/********************************************************************************
* 기      능   : 사무소콤보 변경시 설치PM 콤보값 생성( 계원찬sw 추가 요청 - 2015.10.01)
********************************************************************************/
function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	cb_combo2.value = "";

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S41");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_combo2=ds_zzpmnum_cd"
		, "ZZACTSS="+quote(strCode)
		, "fn_after41"
		, true,true);
}

function fn_after41(errCode, errMsg) {
	gfn_insertEmptyRow( ds_combo2, G_CODE_ALL );
	cb_combo2.value = "";	
}

