function fn_query() {
	ds_list.Clear();
	ds_list2.Clear();

    ed_FileName.value = '';

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps01:PS0102001D_S1");	
  	tit_addSearchActionInfo("ps01:PS0102001D_S2");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list ds_list2=ds_list2"
        , ""
        , ""//"fn_afterQuery"
        , ""
        , ""
        , true);

}
