/********************************************************************************
* 기      능   : 헤더클릭
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{

	if ( nCell == 0 ) {
		return;
	}
	else if ( nCell == 1 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}	


}
/********************************************************************************
* 기      능   : 헤더클릭
********************************************************************************/
function grd_list2_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		return;
	}
	else if ( nCell == 1 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}	
}
function grd_list2_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 5 )
		if ( length(ds_list2.getColumn(nRow, "FNAME")) > 0) {
			// 첨부 파일 처리 

//alert(	ds_list2.getColumn(nRow, "FNAME") );

			saveDialog.FileName = ds_list2.getColumn(nRow, "FNAME");//tit_getLocalFileName(ds_list2.getColumn(nRow, "FNAME"));

			if(ds_list2.GetColumn(nRow, "GBN") == 'A')
			{
				if (saveDialog.open())
				{
					tit_fileRead(
						httpFile
						, "QM"
						, ds_list2.getColumn(nRow, "FNAME_APP")
						, saveDialog.FilePath
						, saveDialog.FileName);
				}
			}
			else
			{
				if (saveDialog.open())
				{
					tit_fileRead(
						httpFile
						, "OCR"
						, ds_list2.GetColumn(nRow, "PSPID")+'/'+ds_list2.GetColumn(nRow, "POSID")+'/'+ds_list2.getColumn(nRow, "FNAME_APP")
						, saveDialog.FilePath
						, saveDialog.FileName);
				}
			}

		}
	}

}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {

	if(gfn_isNull(ed_FileName, true, Static1.Value)) return false; // 첨부파일

	// 그리드 저장 대상 로우
/*	if(ds_list.FindRow("CHECK", 1)==-1)
	{
		gfn_showMsg('CW00046');
		return;
	}

  */  
	if (!gfn_showConfirm("CW10025","저장하시겠습니까?")) 
	{
		return;
	}


	ds_list_tmp.copyF(ds_list);
	ds_list.unFilter();	


	var fileName = "";
		//fileName = left(GetDate(),8)+"_"+right(GetDate(),6)+"_"+ds_list_tmp.getColumn(0, "FNAME");
		fileName = left(GetDate(),8)+"_"+right(GetDate(),6)+"_"+ G_USER_ID +"_"+ds_list_tmp.getColumn(0, "POSID");
		//alert(fileName);
		//ds_list_tmp.SetColumn(0, "FNAME_APP",fileName);
		//ds_list_tmp.setColumn(0, "FNAME_APP", arr[2]);

		var arr = tit_fileWrite(httpFile
						, "QM"
						, ds_list_tmp.getColumn(0, "FPATH")
						, ds_list_tmp.getColumn(0, "FNAME")
						, fileName// 실제 db에 저장될 파일 이름
						, "U"	  // time stamp를 붙이지 않을 경우 U 사용 
						, "");    // ds_list_tmp.getColumn(i, "CS108_QAD") );//삭제할 이전 파일명 

      // ds_list_tmp.SetColumn(0, "FNAME_APP",fileName);
	   ds_list_tmp.setColumn(0, "FNAME_APP", arr[2]);
	//alert( ds_list_tmp.GetColumn(0, "FNAME_APP"));
		if ( arr[0] == "S" ) 
		{
			//디테일 INSERT
			tit_clearActionInfo();
			tit_addMultiActionInfo("ps01:PS0102001D_I1");		                       
			tit_callService(
			  ""
			, ""
			, "ds_list_tmp=ds_list_tmp"
			, ""
			, ""
			, "fn_afterSave" 
			, ""
			, ""
			, true);

		}
		else if ( arr[0] != "S" )
		{
			gfn_showMsg("CW00035"); 
			return;
		} 

}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) 
	{		
		gfn_showMsg("CE00001");		
	} 
	else 
     {
		gfn_showMsg("CI00003");
		ds_list_tmp.DeleteAll();
		fn_query();
	}
}


/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() 
{	

	// 그리드 저장 대상 로우
	if(ds_list2.FindRow("CHECK", 1)==-1)
	{
		//gfn_showMsg('CW00046');
		//return;

		global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
		global.FRM_BOTTOM.st_msg.color = "yellow";       
		global.FRM_BOTTOM.st_msg.value = "하위 리스트에서 정보를 먼저 선택해 주십시오.";
		return ;		


	}

	if (!gfn_showConfirm("CW10025","삭제하시겠습니까?")) 
	{
		return;
	}


//alert(ds_list2.SaveXML());		

	ds_list2.Filter("CHECK==1");
	ds_list2_tmp.copyF(ds_list2);
	ds_list2.unFilter();	


	//디테일 DELETE
	tit_clearActionInfo();
	tit_addMultiActionInfo("ps01:PS0102001D_D1");		                       
	tit_callService(
	  ""
	, ""
	, "ds_list2_tmp=ds_list2_tmp"
	, ""
	, ""
	, "fn_afterDelet" 
	, ""
	, ""
	, true);		


}

/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelet(errCode, errMsg) {
	if ( errCode!= 0 ) 
	{		
		gfn_showMsg("CE00001");		
	} 
	else 
	{
		gfn_showMsg("CI00004");


		//같은 호기에 동일 파일명이 없을시 서버파일 삭제 체크
		for(var i=0; i<ds_list2_tmp.GetRowCount(); i++)
		{
			ds_cond3.ClearData();
			ds_cond3.AddRow();
			ds_cond3.setColumn(0, "ZZGUBUN", ds_list2_tmp.GetColumn(i, "ZZGUBUN"));
			ds_cond3.setColumn(0, "PSPID", ds_list2_tmp.GetColumn(i, "PSPID"));
			ds_cond3.setColumn(0, "POSID", ds_list2_tmp.GetColumn(i, "POSID"));
			ds_cond3.setColumn(0, "FNAME", ds_list2_tmp.GetColumn(i, "FNAME"));
//alert(ds_cond3.SaveXML());	
			tit_clearActionInfo();
			tit_addSearchActionInfo("ps01:PS0102001D_S3");		                         	
			tit_callService(
				""
				, ""
				, "ds_cond3=ds_cond3"
				, "ds_list3=ds_list3"
				, ""
				, ""
				, ""
				, ""
				, true);

			if( ds_list3.GetColumn(0, "CNT") = 0 )
			{

			    //파일삭제
				tit_fileDelete("QM", ds_list2_tmp.GetColumn(i, "FNAME_APP"), true);					
			}	


		}		


		ds_list2_tmp.DeleteAll();
		fn_query();
	}
}
