/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	if(gfn_isNull(div_pop.ed_pjt, true, div_pop.Static1.Value)) return false; 
	if(gfn_isNull(div_pop.ed_hogi, true, div_pop.st_hogi.Value)) return false;	

	//if(gfn_isNull(div_pop.cb_gbn, true, div_pop.Static4.Value)) return false;	// BD 시리얼번호 누락


	if ( !gfn_isChangeDs(ds_master) ) 
	{
		if ( !gfn_isChangeDs(ds_sill, true, "FLAG") ) 
		{
			return;
		}
	}

	for(var i=0; i<ds_sill.GetRowCount(); i++)
	{		
		// 필수 입력 항목	
		if(length(ds_sill.GetColumn(i, "CODE1"))<=0 )
		{
			var arr = [parseInt(i) + 1, "검사Part"];
			gfn_showMsg("CW00224", arr);
			return;
		} 			

		if(length(ds_sill.GetColumn(i, "CODE2"))<=0 )
		{
			var arr = [parseInt(i) + 1, "검사항목"];
			gfn_showMsg("CW00224", arr);
			return;
		} 

		if(length(ds_sill.GetColumn(i, "CODE3"))<=0 )
		{
			var arr = [parseInt(i) + 1, "검사지적사항"];
			gfn_showMsg("CW00224", arr);
			return;
		} 

	}


	//해당데이타를 저장하시겠습니까?
	if(!gfn_showConfirm( "CI00014", "해당 데이터" )) return;

	// 저장시 마스크 에디트의 숫자값 넣어주기 null일때 
	/*ds_master.SetColumn(0, "SIZE1", decode(div_pop.MaskEdit0.value,'',0,null,0,div_pop.MaskEdit0.value));
	ds_master.SetColumn(0, "SIZE2", decode(div_pop.MaskEdit1.value,'',0,null,0,div_pop.MaskEdit1.value));
	ds_master.SetColumn(0, "SIZE3", decode(div_pop.MaskEdit2.value,'',0,null,0,div_pop.MaskEdit2.value));
	ds_master.SetColumn(0, "SIZE4", decode(div_pop.MaskEdit3.value,'',0,null,0,div_pop.MaskEdit3.value));
	ds_master.SetColumn(0, "SIZE5", decode(div_pop.MaskEdit4.value,'',0,null,0,div_pop.MaskEdit4.value));
	ds_master.SetColumn(0, "SIZE6", decode(div_pop.MaskEdit5.value,'',0,null,0,div_pop.MaskEdit5.value));
	ds_master.SetColumn(0, "SIZE7", decode(div_pop.MaskEdit6.value,'',0,null,0,div_pop.MaskEdit6.value));
	ds_master.SetColumn(0, "SIZE8", decode(div_pop.MaskEdit7.value,'',0,null,0,div_pop.MaskEdit7.value));
	ds_master.SetColumn(0, "SIZE9", decode(div_pop.MaskEdit8.value,'',0,null,0,div_pop.MaskEdit8.value));
	*/



	tit_clearActionInfo();	 
	tit_addSelectExActionInfo("ps01:PS0102001S_S2", "ps01:PS0102001S_I2", "ps01:PS0102001S_U2");


	tit_addSaveActionInfo(
          "ps01:PS0102001L_I1"
        , "ps01:PS0102001L_U1"
        , "ps01:PS0102001L_D1"
        , "FLAG"
        , ""
        , -1
        , "");

	tit_callService(
        ""
        , ""
        , "ds_master=ds_master ds_sill=ds_sill:A"
        , ""
        , ""
        , "fn_afterSave"
        , ""
        , "" 
        );	


}	

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) 
{
	if ( errCode!= 0 ) 
	{
		gfn_showAlert("CE00001");
	} 
	else 
	{
		gfn_showAlert("CI00003");
		//SAVEFLG = 'S';
		//SELFLG = "Y";
		fn_query();
	}
}
