/********************************************************************************
* 기      능   : Q.C OCR  완성검사 프린트 테스트
********************************************************************************/
function div_pop_btn_qcPrint_OnClick(obj)
{
	// 설치검사 차수가 4차인지 확인 (4차수이면 : 리셋정보 출력)
	var sResetIndc = "";
	// ZQMT055(점검항목 헤더 확인) - 헤더에 데이터가 없으면 최초 출력, 데이터가 있으면 보완,수정으로 출력
	var sInitIndc = "";

	ds_master.SetColumn(0, "EL_TYPE", "EL");

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps01:PS0102001L_S7");	
	tit_addSearchActionInfo("ps01:PS0102001L_S9");	// 점검항목 헤더(ZQMT055) select
	tit_addSearchActionInfo("ps01:PS0102001L_S10"); // 설치검사 차수 4차인지 (검사유형 : 완성)
	tit_addSearchActionInfo("ps01:PS0102001L_S11"); // ZQMT055 (성적서 HEADER)
	tit_addSearchActionInfo("ps01:PS0102001L_S12"); // ZQMT056 (성적서 DETAIL) 
	tit_callService(
        ""
        , ""
        , "ds_master=ds_master"
        , "ds_print=ds_print ds_chkHeader=ds_chkHeader ds_chasu=ds_chasu ds_header=ds_header ds_detail_tmp=ds_detail_tmp"
        , ""
        , ""
        , ""
        , ""
        , true);

    var sPosid = ds_master.GetColumn(0, "POSID");
    // DETAIL정보가 존재하면 컬럼 동적 생성처리
    // 1. 컬럼정보 생성

    ds_detail.Clear();
	ds_detail.addColumn("POSID");

    for(var i=0; i<ds_detail_tmp.GetRowCount();i++)
    {
		var sLcd = ds_detail_tmp.GetColumn(i, "LCD");
		var sMcd = ds_detail_tmp.GetColumn(i, "MCD");
		var sScd = ds_detail_tmp.GetColumn(i, "SCD");

		var sKey = sLcd+sMcd+sScd;
		ds_detail.addColumn("COL_"+sKey+"_IMPRO");     // 보완여부 Y/N
		ds_detail.addColumn("COL_"+sKey+"_MOD");       // 수정 여부 Y/N
		ds_detail.addColumn("COL_"+sKey+"_INPUT_VAL"); // 입력 값
		ds_detail.addColumn("COL_"+sKey+"_PASSYN");    // 합격/불합격 Y/N
		ds_detail.addColumn("COL_"+sKey+"_FNAME");    // 파일명.png

    }

    var nAddRow = ds_detail.addRow();
    // 2. 로우정보 생성
    for(var i=0; i<ds_detail_tmp.GetRowCount();i++)
    {
		var sLcd = ds_detail_tmp.GetColumn(i, "LCD");
		var sMcd = ds_detail_tmp.GetColumn(i, "MCD");
		var sScd = ds_detail_tmp.GetColumn(i, "SCD");
		var sKey = sLcd+sMcd+sScd;

		var nSearchRow = ds_detail_tmp.SearchRow("LCD=='"+sLcd+"' && MCD=='"+sMcd+"' && SCD=='"+sScd+"'");

		var sImpro = "";
		var sMod = "";
		var sInputVal = "";
		var sPassYn = "";
		var sFname = "";

		if(nSearchRow >= 0)
		{
			sImpro = ds_detail_tmp.getColumn(nSearchRow, "IMPRO");
			sMod   = ds_detail_tmp.getColumn(nSearchRow, "MOD");
			sInputVal = ds_detail_tmp.getColumn(nSearchRow, "INPUT_VAL");
			sPassYn   = ds_detail_tmp.getColumn(nSearchRow, "PASSYN");
			sFname    = ds_detail_tmp.getColumn(nSearchRow, "FNAME");
		}

		ds_detail.SetColumn(nAddRow, "COL_"+sKey+"_IMPRO", sImpro);
		ds_detail.SetColumn(nAddRow, "COL_"+sKey+"_MOD", sMod);
		ds_detail.SetColumn(nAddRow, "COL_"+sKey+"_INPUT_VAL", sInputVal);
		ds_detail.SetColumn(nAddRow, "COL_"+sKey+"_PASSYN", sPassYn);
		ds_detail.SetColumn(nAddRow, "COL_"+sKey+"_FNAME", sFname);
    }

    //Trace(ds_header.saveXML());
    //Trace(ds_detail.saveXML());

    var nChkHeadCnt = ds_chkHeader.GetColumn(0, "CNT"); // 점검항목 헤더 데이터 유무
    var nSeq        = toNumber(ds_chasu.GetColumn(0, "SEQ"));     // SEQ


    //if(true)
    if(nChkHeadCnt > 0)	// 헤더에 데이터가 있으면 보완, 수정
    {
		sInitIndc = "MODIFY";
    }
    else	// 데이터가 없으면 최초
    {
		sInitIndc = "INIT";
    }

    // 4차수인지 검사
    if(nSeq == 4)
    {
		sResetIndc = "RESET";
    }


    // 리셋인 경우 -> 리셋 출력
    if(sResetIndc == "RESET")
    {
		ds_print.SetColumn(0, "RESET_INDC", "Y");
		var sRset = ds_header.GetColumn(0, "R_SET");

		if(sRset == 'Y')
		{
			ds_print.SetColumn(0, "RESET_MOD", "Y");
		}
		Dialog("PS01::PS0102006P.xml", "", "Resize=true", -1, -1);
    }
    else
    {
		if(sInitIndc == "INIT")	//최초 출력
		{
			Dialog("PS01::PS0102006P.xml", "", "Resize=true", -1, -1);
		}
		else	// 보완 / 수정
		{
			ds_print.SetColumn(0, "MOD_INDC", "Y");
			Dialog("PS01::PS0102006P.xml", "", "Resize=true", -1, -1);
		}
    }


}

/********************************************************************************
* 기      능   : Q.C OCR  중간검사 프린트 테스트
********************************************************************************/
function div_pop_btn_qcPrint2_OnClick(obj)
{

	// 설치검사 차수가 4차인지 확인 (4차수이면 : 리셋정보 출력)
	var sResetIndc = "";
	// ZQMT055(점검항목 헤더 확인) - 헤더에 데이터가 없으면 최초 출력, 데이터가 있으면 보완,수정으로 출력
	var sInitIndc = "";

	ds_master.SetColumn(0, "EL_TYPE", "RA");


	tit_clearActionInfo();
	tit_addSearchActionInfo("ps01:PS0102001L_S7");	
	tit_addSearchActionInfo("ps01:PS0102001L_S9");	// 점검항목 헤더(ZQMT055) select
	tit_addSearchActionInfo("ps01:PS0102001L_S10"); // 설치검사 차수 4차인지 (검사유형 : 완성)
	tit_addSearchActionInfo("ps01:PS0102001L_S11"); // ZQMT055 (성적서 HEADER)
	tit_addSearchActionInfo("ps01:PS0102001L_S12"); // ZQMT056 (성적서 DETAIL)
	 tit_addSearchActionInfo("ps01:PS0102001L_S13"); // JOINT DATA 가져오기
	tit_callService(
        ""
        , ""
        , "ds_master=ds_master"
        , "ds_print=ds_print ds_chkHeader=ds_chkHeader ds_chasu=ds_chasu ds_header=ds_header ds_detail_tmp=ds_detail_tmp ds_joint_tmp=ds_joint_tmp"
        , ""
        , ""
        , ""
        , ""
        , true);

    var sPosid = ds_master.GetColumn(0, "POSID");
    // DETAIL정보가 존재하면 컬럼 동적 생성처리
    // 1. 컬럼정보 생성

    ds_detail.Clear();
	ds_detail.addColumn("POSID");

    for(var i=0; i<ds_detail_tmp.GetRowCount();i++)
    {
		var sLcd = ds_detail_tmp.GetColumn(i, "LCD");
		var sMcd = ds_detail_tmp.GetColumn(i, "MCD");
		var sScd = ds_detail_tmp.GetColumn(i, "SCD");

		var sKey = sLcd+sMcd+sScd;
		ds_detail.addColumn("COL_"+sKey+"_IMPRO");     // 보완여부 Y/N
		ds_detail.addColumn("COL_"+sKey+"_MOD");       // 수정 여부 Y/N
		ds_detail.addColumn("COL_"+sKey+"_INPUT_VAL"); // 입력 값
		ds_detail.addColumn("COL_"+sKey+"_PASSYN");    // 합격/불합격 Y/N
		ds_detail.addColumn("COL_"+sKey+"_FNAME");    // 파일명.png

    }

    var nAddRow = ds_detail.addRow();
    // 2. 로우정보 생성
    for(var i=0; i<ds_detail_tmp.GetRowCount();i++)
    {
		var sLcd = ds_detail_tmp.GetColumn(i, "LCD");
		var sMcd = ds_detail_tmp.GetColumn(i, "MCD");
		var sScd = ds_detail_tmp.GetColumn(i, "SCD");
		var sKey = sLcd+sMcd+sScd;

		var nSearchRow = ds_detail_tmp.SearchRow("LCD=='"+sLcd+"' && MCD=='"+sMcd+"' && SCD=='"+sScd+"'");

		var sImpro = "";
		var sMod = "";
		var sInputVal = "";
		var sPassYn = "";
		var sFname = "";

		if(nSearchRow >= 0)
		{
			sImpro = ds_detail_tmp.getColumn(nSearchRow, "IMPRO");
			sMod   = ds_detail_tmp.getColumn(nSearchRow, "MOD");
			sInputVal = ds_detail_tmp.getColumn(nSearchRow, "INPUT_VAL");
			sPassYn   = ds_detail_tmp.getColumn(nSearchRow, "PASSYN");
			sFname    = ds_detail_tmp.getColumn(nSearchRow, "FNAME");
		}

		ds_detail.SetColumn(nAddRow, "COL_"+sKey+"_IMPRO", sImpro);
		ds_detail.SetColumn(nAddRow, "COL_"+sKey+"_MOD", sMod);
		ds_detail.SetColumn(nAddRow, "COL_"+sKey+"_INPUT_VAL", sInputVal);
		ds_detail.SetColumn(nAddRow, "COL_"+sKey+"_PASSYN", sPassYn);
		ds_detail.SetColumn(nAddRow, "COL_"+sKey+"_FNAME", sFname);
    }

    //24   12 24 36 

    // Default 값 (단수에 따른 Report 한페이지 가득차도록 JOINT 개수 생성)
    var nRowCount = Ceil(ToNumber(ds_print.getColumn(0, "EL_ERBQ"))/2);

    ds_joint.ClearData();

    for(var i=0; i<nRowCount; i++)
    {
		var nRow = ds_joint.addRow();
		ds_joint.SetColumn(nRow, "SEQ", nRow+1);
    }


    var nCnt =  Floor(nRowCount / 12);
    var nLoop = 0;
    //Trace("1 : "+nCnt);

    if(nCnt == 0)
    {
		nLoop = 12 - nRowCount;
    }
    else
    {
		if(nRowCount == nCnt*12)
		{
			nLoop = 0;
		}
		else
		{
			nLoop = 12*ToNumber(nCnt+1) - ToNumber(nRowCount);
		}

    }
    //Trace("2 : "+nLoop);

    for(var i=0 ;i<nLoop; i++)
    {
		var nRow = ds_joint.addRow();
		ds_joint.SetColumn(nRow, "SEQ", nRow+1);
    }


    // 실제 Joint 데이터와 매칭
    for(var j=0; j<ds_joint_tmp.GetRowCount(); j++)
    {
		var nDataRow = ds_joint_tmp.GetColumn(j, "SEQ");
		var sCol1 	 = ds_joint_tmp.GetColumn(j, "COL_1");
		var sCol2 	 = ds_joint_tmp.GetColumn(j, "COL_2");
		var sCol3 	 = ds_joint_tmp.GetColumn(j, "COL_3");
		var sCol4 	 = ds_joint_tmp.GetColumn(j, "COL_4");
		var sCol5 	 = ds_joint_tmp.GetColumn(j, "COL_5");
		var sCol6 	 = ds_joint_tmp.GetColumn(j, "COL_6");
		var sCol7 	 = ds_joint_tmp.GetColumn(j, "COL_7");

		var sCol1_Fname 	 = ds_joint_tmp.GetColumn(j, "COL_1_FNAME");
		var sCol2_Fname 	 = ds_joint_tmp.GetColumn(j, "COL_2_FNAME");
		var sCol3_Fname 	 = ds_joint_tmp.GetColumn(j, "COL_3_FNAME");
		var sCol4_Fname 	 = ds_joint_tmp.GetColumn(j, "COL_4_FNAME");
		var sCol5_Fname 	 = ds_joint_tmp.GetColumn(j, "COL_5_FNAME");
		var sCol6_Fname 	 = ds_joint_tmp.GetColumn(j, "COL_6_FNAME");

		var nFindRow = ds_joint.SearchRow("SEQ == '"+ToNumber(nDataRow)+"'");
		if(nFindRow >= 0)
		{
			ds_joint.SetColumn(nFindRow, "COL_1", sCol1);
			ds_joint.SetColumn(nFindRow, "COL_2", sCol2);
			ds_joint.SetColumn(nFindRow, "COL_3", sCol3);
			ds_joint.SetColumn(nFindRow, "COL_4", sCol4);
			ds_joint.SetColumn(nFindRow, "COL_5", sCol5);
			ds_joint.SetColumn(nFindRow, "COL_6", sCol6);
			ds_joint.SetColumn(nFindRow, "COL_7", sCol7);

			ds_joint.SetColumn(nFindRow, "COL_1_FNAME", sCol1_Fname);
			ds_joint.SetColumn(nFindRow, "COL_2_FNAME", sCol2_Fname);
			ds_joint.SetColumn(nFindRow, "COL_3_FNAME", sCol3_Fname);
			ds_joint.SetColumn(nFindRow, "COL_4_FNAME", sCol4_Fname);
			ds_joint.SetColumn(nFindRow, "COL_5_FNAME", sCol5_Fname);
			ds_joint.SetColumn(nFindRow, "COL_6_FNAME", sCol6_Fname);
		}
    }


    var nChkHeadCnt = ds_chkHeader.GetColumn(0, "CNT"); // 점검항목 헤더 데이터 유무
    var nSeq        = toNumber(ds_chasu.GetColumn(0, "SEQ"));     // SEQ


    if(nChkHeadCnt > 0)	// 헤더에 데이터가 있으면 보완, 수정
    {
		sInitIndc = "MODIFY";
    }
    else	// 데이터가 없으면 최초
    {
		sInitIndc = "INIT";
    }

    // 4차수인지 검사
    if(nSeq == 4)
    {
		sResetIndc = "RESET";
    }


    // 리셋인 경우 -> 리셋 출력
    if(sResetIndc == "RESET")
    {
		ds_print.SetColumn(0, "RESET_INDC", "Y");
		var sRset = ds_header.GetColumn(0, "R_SET");

		if(sRset == 'Y')
		{
			ds_print.SetColumn(0, "RESET_MOD", "Y");
		}


		Dialog("PS01::PS0102007P.xml", "", "Resize=true", -1, -1);
    }
    else
    {
		if(sInitIndc == "INIT")	//최초 출력
		{
			Dialog("PS01::PS0102007P.xml", "", "Resize=true", -1, -1);
		}
		else	// 보완 / 수정
		{
			ds_print.SetColumn(0, "MOD_INDC", "Y");
			Dialog("PS01::PS0102007P.xml", "", "Resize=true", -1, -1);
		}
    }

}

