/********************************************************************************
* 기      능   : 차수 삭제 버튼 클릭
********************************************************************************/
function div_pop_btn_del_chkdt_OnClick(obj)
{

	if(!confirm("차수 삭제 진행시 해당 첨부파일 및 관련 DATA는 삭제 처리됩니다. 계속 진행 하시겠습니까?")) {
		return;	
	}

	if (div_pop.cb_chkdt.Index == 0 ) {	
		F_IS_MASTER = true;	
	} else {
		F_IS_MASTER = false;
	}

	ds_master.SetColumn(0, "EL_TYPE", "EL");

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps01:PS0102001L_S16"); // ZQMT055H (성적서 HEADER)
	tit_addSearchActionInfo("ps01:PS0102001L_S17"); // ZQMT056H (성적서 DETAIL) 
	tit_addSearchActionInfo("ps01:PS0102001L_S18"); // ZQMT055F ( 사진파일 ) 
	tit_callService(
        ""
        , ""
        , "ds_master=ds_master"
        , "ds_header=ds_header ds_detail_tmp=ds_detail_tmp ds_file=ds_file"
        , ""
        , "fn_afterQueryDelChkdt"
        , ""
        , ""
        , true);	

}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQueryDelChkdt(errCode, errMsg) {

	var rowcnt1 = ds_header.rowcount;
	var rowcnt2 = ds_detail_tmp.rowcount;
	var rowcnt3 = ds_file.rowcount;	

	// ZQMT055H 파일삭제 ds_header
	if ( rowcnt1 > 0 ) { 
		var sFileName1 = ds_header.GetColumn(ds_header.currow, "FNAME1");
		var sFileName2 = ds_header.GetColumn(ds_header.currow, "FNAME2");
		var sFileName3 = ds_header.GetColumn(ds_header.currow, "FNAME3");		
		var sFilepath = ds_header.GetColumn(ds_header.currow, "FILEPATH");

		if(length(sFileName1) != 0) {
			var arr = tit_fileDelete("OCR",sFilepath+'/'+ds_header.getColumn(ds_header.currow, "FNAME1"), false);
		}
		if(length(sFileName2) != 0) {
			var arr = tit_fileDelete("OCR",sFilepath+'/'+ds_header.getColumn(ds_header.currow, "FNAME2"), false);
		}
		if(length(sFileName3) != 0) {
			var arr = tit_fileDelete("OCR",sFilepath+'/'+ds_header.getColumn(ds_header.currow, "FNAME3"), false);
		}
	}

	// ZQMT056H 파일삭제 ds_detail_tmp
	for( var i = 0 ;  i < rowcnt2 ; i++ ) { 			
		var sFileName = ds_detail_tmp.GetColumn(i, "FNAME");

		var sFilepath = ds_detail_tmp.GetColumn(i, "FILEPATH");

		if(length(sFileName) != 0) {
			var arr = tit_fileDelete("OCR",sFilepath+'/'+ds_detail_tmp.getColumn(i, "FNAME"), false);
		}
	}

	// ZQMT055F 파일삭제 ds_file 
	for( var i = 0 ;  i < rowcnt3 ; i++ ) { 			
		var sFileName = ds_file.GetColumn(i, "FNAME");

		var sFilepath = ds_file.GetColumn(i, "FILEPATH");

		if(length(sFileName) != 0) {
			var arr = tit_fileDelete("OCR",sFilepath+'/'+ds_file.getColumn(i, "FNAME"), false);
		}
	}

	// 삭제
	tit_clearActionInfo();

	if(F_IS_MASTER) { 
		tit_addSingleActionInfo("ps01:PS0102001L_D4"); //ZQMT055
	}
	tit_addSingleActionInfo("ps01:PS0102001L_D5");
	tit_addSingleActionInfo("ps01:PS0102001L_D6");
	if(F_IS_MASTER) { 
		tit_addSingleActionInfo("ps01:PS0102001L_D7"); // ZQMT056
	}
	tit_addSingleActionInfo("ps01:PS0102001L_D8");

	tit_callService(
        ""
        , ""
        , "ds_master=ds_master"
        , ""
        , ""
        , "fn_afterDeleteChkdt"
		, ""
		, ""
        , true);
}

/********************************************************************************
* 기      능   : 차수 삭제 Callback  
********************************************************************************/
function fn_afterDeleteChkdt(errCode, errMsg) 
{

	if (F_IS_MASTER && ds_code_chkdt.rowcount > 1  ) { // 마스터테이블 삭제 여부 

		// ZQMT055, ZQMT056 다음 차수 데이터 SELET INSERT 

		tit_clearActionInfo();
		tit_addSingleActionInfo("ps01:PS0102001L_I3");
		tit_addSingleActionInfo("ps01:PS0102001L_I4");

		tit_callService(
			""
			, ""
			, "ds_master=ds_master"
			, ""
			, ""
			, "fn_afterInsertRpcTest"
			, ""
			, ""
			, true);

	} else {

		if ( errCode != 0 ) 
		{
			gfn_showMsg("CE00001");
		} 
		else 
		{
			gfn_showMsg("CI00004");
			fn_query();
		}	
	}	
}	

/********************************************************************************
* 기      능   : 차수 Master 입력 Callback  
********************************************************************************/
function fn_afterInsertRpcTest(errCode, errMsg) 
{
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00004");				   
		fn_query();
	}	
}

/********************************************************************************
