********************************************************************************/
function div_pop_cb_chkdt_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	if ( G_USER_ID == '2029847' ) { 
		div_pop.btn_del_chkdt.Enable = true;
	} else {
		div_pop.btn_del_chkdt.Enable = false;
	}

	ds_master.SetColumn(0, "EL_TYPE", "EL");

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps01:PS0102001L_S12"); // ZQMT056 (성적서 DETAIL) 	
	tit_addSearchActionInfo("ps01:PS0102001L_S19"); // 	

	tit_callService(
        ""
        , ""
        , "ds_master=ds_master"
        , "ds_detail_tmp=ds_detail_tmp ds_test=ds_test"
        , ""
        , ""
        , ""
        , ""
        , true);	
}


/********************************************************************************
* 기      능   : 검사 차수 값 변경 시
********************************************************************************/
function ds_test_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{	
	// ZQMT056H 수정
	if ( substr(strColumnID, 0, 3) == "COL" ) {

		var sLcd = substr(strColumnID, 4, 2);
		var sMcd = substr(strColumnID, 6, 2);
		var sScd = substr(strColumnID, 8, 2);

		var nSearchRow = ds_detail_tmp.SearchRow("LCD=='"+sLcd+"' && MCD=='"+sMcd+"' && SCD=='"+sScd+"'");		

		if(nSearchRow >= 0){
			ds_detail_tmp.SetColumn(nSearchRow, "INPUT_VAL" , varNewValue);
		} 	
	} else if( strColumnID != "DIPBXNUMB" )  { // 비상통화장치번호 업체입력 제외

		obj.fireevent = false;

		ds_test.SetColumn(nRow,"FLAG","U");
		ds_test.SetColumn(nRow,"CHK",1);

		obj.fireevent = true;		
	}
}

function ds_detail_tmp_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;

	ds_detail_tmp.SetColumn(nRow,"FLAG","U");
	ds_detail_tmp.SetColumn(nRow,"CHK",1);

	obj.fireevent = true;	
}


/********************************************************************************
* 기      능   : 건축전원 값 변경 시
********************************************************************************/
function div_pop_div_detail_chk_power_a1_OnClick(obj,strValue)
{
	if(strValue == "Y" ) {
		ds_test.SetColumn(ds_test.currow, "POWER_B1", ""  );
		ds_test.SetColumn(ds_test.currow, "POWER", "A1"  );
	} else {
		ds_test.SetColumn(ds_test.currow, "POWER", ""  );
	}
}

function div_pop_div_detail_chk_power_b1_OnClick(obj,strValue)
{
	if(strValue == "Y" ) {
		ds_test.SetColumn(ds_test.currow, "POWER_A1", ""  );
		ds_test.SetColumn(ds_test.currow, "POWER", "B1"  );
	} else {
		ds_test.SetColumn(ds_test.currow, "POWER", ""  );
	}
}
