	tit_addSearchActionInfo("ps01:PS0102001L_S3");
	tit_addSearchActionInfo("ps01:PS0102001L_S4");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"  //""
		, "ds_code1=ds_code1 ds_code2=ds_code2 ds_code3=ds_code3"
		, ""
		, ""//"fn_afterCode2Search"
		, ""
		, ""
		, true);
	//=========================================
	ds_code2.filter("HIG_CODE == " + quote( PRE_PJT) );
	ds_code3.filter("HIG_CODE == " + quote( PRE_PJT) );

	fn_query();


}
